from flask import Flask, request, jsonify
import threading
from werkzeug.serving import make_server
from waitress import serve

class FlaskAPI:
    def __init__(self):
        self.app = Flask(__name__)
        self.server_thread = None
        self._setup_routes()

    def _setup_routes(self):
        @self.app.route("/status", methods=["POST"])
        def status():
            return jsonify({"response": True})

        @self.app.route("/input", methods=["POST"])
        def input():
            data = request.json
            text = data["text"]
            screen = data["screen"]
            talk = data["talk"]
            return self._the_input(text, screen, talk)

        @self.app.route("/stop_server", methods=["POST"])
        def stop_server():
            self.stop_api()
            exit(0)

    def _the_input(self, text, screen, talk):
        print("Input:", text)
        from .agent.process import process_text_api
        from .utils.db import screenshot_path

        if screen != "true":
            result = process_text_api(text, None)
        else:
            import pyautogui
            screenshot = pyautogui.screenshot()
            screenshot.save(screenshot_path)
            result = process_text_api(text, screenshot_path)

        return jsonify({"response": result})

    def start_api(self, api=False):
        if api == False:
            if self.server_thread is None:
                self.server_thread = ServerThread(self.app, "0.0.0.0", 7541)
                self.server_thread.start()
                print("API started")
            else:
                print("API is already running")
        else:
            serve(self.app, host="0.0.0.0", port=7541)

    def stop_api(self):
        if self.server_thread is not None:
            self.server_thread.shutdown()
            self.server_thread.join()
            self.server_thread = None
            print("API stopped")
        else:
            print("API is not running")

class ServerThread(threading.Thread):
    def __init__(self, app, host, port):
        threading.Thread.__init__(self)
        self.srv = make_server(host, port, app)
        self.ctx = app.app_context()
        self.ctx.push()

    def run(self):
        print("Starting server")
        self.srv.serve_forever()

    def shutdown(self):
        print("Stopping server")
        self.srv.shutdown()
