from flask import Flask, request, render_template

class ONNXModifierServer:
    def __init__(self):
        self.app = Flask(__name__)
        self.onnx_modifier = None
        self._setup_routes()

    def _setup_routes(self):
        self.app.add_url_rule('/', 'index', self.index)
        self.app.add_url_rule('/open_model', 'open_model', self.open_model, methods=['POST'])
        self.app.add_url_rule('/download', 'modify_and_download_model', self.modify_and_download_model, methods=['POST'])

    def index(self):
