from flask import Flask, render_template, send_file, redirect, request, send_from_directory, url_for, abort
from flask_httpauth import HTTPBasicAuth
from werkzeug.utils import secure_filename
from werkzeug.security import generate_password_hash, check_password_hash
from werkzeug.serving import run_simple

def create_flask_app(directory, port, password, ssl):
    app = Flask(__name__)
    auth = HTTPBasicAuth()

    @app.route('/', defaults={'path': None})
    @app.route('/<path:path>')
    @auth.login_required
    def home(path):
        if path and is_valid_subpath(path, directory):
            path = os.path.normpath(path)
            requested_path = os.path.join(directory, path)

            if os.path.isdir(requested_path):
                back = get_parent_directory(requested_path, directory)
                is_subdirectory = True
            elif os.path.isfile(requested_path):
                if request.args.get('view') is None:
                    send_as_attachment = True
                else:
                    send_as_attachment = False

                (filename, extension) = os.path.splitext(requested_path)
                if extension == '':
                    mimetype = 'text/plain'
                else:
                    mimetype = None

                try:
                    return send_file(requested_path, mimetype=mimetype, as_attachment=send_as_attachment)
                except PermissionError:
                    abort(403, 'Read Permission Denied: ' + requested_path)
        else:
            is_subdirectory = False
            requested_path = directory
            back = ''

        if os.path.exists(requested_path):
            try:
                directory_files = process_files(os.scandir(requested_path), directory)
            except PermissionError:
                abort(403, 'Read Permission Denied: ' + requested_path)

            return render_template('home.html', files=directory_files, back=back,
                                 directory=requested_path, is_subdirectory=is_subdirectory, version=VERSION)
        else:
            return redirect('/')

    @app.route('/upload', methods=['POST'])
    @auth.login_required
    def upload():
        if request.method == 'POST':
            if 'file' not in request.files:
                return redirect(request.referrer)

            path = request.form['path']
            if not is_valid_upload_path(path, directory):
                return redirect(request.referrer)

            for file in request.files.getlist('file'):
                if file.filename == '':
                    return redirect(request.referrer)

                if file:
                    filename = secure_filename(file.filename)
                    full_path = os.path.join(path, filename)
                    try:
                        file.save(full_path)
                    except PermissionError:
                        abort(403, 'Write Permission Denied: ' + full_path)

            return redirect(request.referrer)

    users = {
        '': generate_password_hash(password)
    }

    @auth.verify_password
    def verify_password(username, password):
        if password:
            if username in users:
                return check_password_hash(users.get(username), password)
            return False
        else:
            return True

    ssl_context = 'adhoc' if ssl else None
    run_simple("0.0.0.0", int(port), app, ssl_context=ssl_context)
