from flask import Flask, Response, request
import re
import os
import base64
import json
import librato
import statsd
from blueprint import Blueprint
import backend

class MeteredResponse(Response):
    def __init__(self, *args, **kwargs):
        super(MeteredResponse, self).__init__(*args, **kwargs)
        content_length = len(kwargs.get('response', ''))
        if 0 < content_length:
            librato.count('blueprint-io-server.bandwidth.out', content_length)
            statsd.update('blueprint-io-server.bandwidth.out', content_length)

class BlueprintServer:
    def __init__(self):
        self.app = Flask(__name__)
        self._setup_routes()
        self.browser_pattern = re.compile(r'Chrome|Gecko|Microsoft|Mozilla|Safari|WebKit')

    def _setup_routes(self):
        self.app.route('/secret', methods=['GET'])(self.secret)
        self.app.route('/<secret>/<name>', methods=['GET'])(self.get_blueprint)
        self.app.route('/<secret>/<name>', methods=['PUT'])(self.put_blueprint)
        self.app.route('/<secret>/<name>/<sha>.tar', methods=['GET'])(self.get_tarball)
        self.app.route('/<secret>/<name>/<sha>.tar', methods=['PUT'])(self.put_tarball)
        self.app.route('/<secret>/<name>/<name2>.sh', methods=['GET'])(self.sh)
        self.app.route('/<secret>/<name>/user-data.sh', methods=['GET'])(self.user_data)

    def _blueprint(self, secret, name):
        data = backend.get_blueprint(secret, name)
        if data is None:
            return None
        elif data is False:
            return False
        b = Blueprint()
        b.name = name
        b.update(json.loads(data))
        return b

    def _server(self):
        return request.url_root

    def _validate_secret(self, secret):
        if re.match(r'^[0-9A-Za-z_-]{64}$', secret) is None:
            abort(400)

    def _validate_name(self, name):
        if re.search(r'[/ \t\r\n]', name) is not None:
            abort(400)

    def _validate_sha(self, sha):
        if re.match(r'^[0-9a-f]{40}$', sha) is None:
            abort(400)

    def secret(self):
        while True:
            s = base64.urlsafe_b64encode(os.urandom(48))
            try:
                iter(backend.list(s)).next()
            except StopIteration:
                break
        return MeteredResponse(response='{0}\n'.format(s),
                             status=201,
                             content_type='text/plain')

    def get_blueprint(self, secret, name):
        self._validate_secret(secret)
        self._validate_name(name)

        content_length = backend.head_blueprint(secret, name)
        if content_length is None:
            abort(404)

        if self.browser_pattern.search(request.environ.get('HTTP_USER_AGENT', '')) or 'html' == request.args.get('format'):
            librato.count('blueprint-io-server.renders')
            statsd.increment('blueprint-io-server.renders')
            return render_template('blueprint.html', b=self._blueprint(secret, name))
        else:
            librato.count('blueprint-io-server.requests.get')
            statsd.increment('blueprint-io-server.requests.get')
            librato.count('blueprint-io-server.bandwidth.out', content_length)
            statsd.update('blueprint-io-server.bandwidth.out', content_length)
            return redirect(backend.url_for_blueprint(secret, name), code=301)

    def put_blueprint(self, secret, name):
        self._validate_secret(secret)
        self._validate_name(name)

        librato.count('blueprint-io-server.bandwidth.in', request.content_length)
        statsd.update('blueprint-io-server.bandwidth.in', request.content_length)

        try:
            for k in request.json.iterkeys():
                if k not in ('arch', 'files', 'packages', 'services', 'sources'):
                    abort(400)
        except ValueError:
            abort(400)

        b = self._blueprint(secret, name)
        if b is not None and b is not False:
            for filename in set(b.sources.itervalues()) - set(request.json.get('sources', {}).itervalues()):
                backend.delete_tarball(secret, name, filename[0:-4])

        if not backend.put_blueprint(secret, name, request.data):
            abort(502)

        return MeteredResponse(response='',
                             status=202,
                             content_type='text/plain')

    def get_tarball(self, secret, name, sha):
        self._validate_secret(secret)
        self._validate_name(name)
        sha = sha.lower()
        self._validate_sha(sha)

        content_length = backend.head_tarball(secret, name, sha)
        if content_length is None:
            abort(404)

        librato.count('blueprint-io-server.requests.get')
        statsd.increment('blueprint-io-server.requests.get')
        librato.count('blueprint-io-server.bandwidth.out', content_length)
        statsd.update('blueprint-io-server.bandwidth.out', content_length)

        return redirect(backend.url_for_tarball(secret, name, sha), code=301)

    def put_tarball(self, secret, name, sha):
        self._validate_secret(secret)
        self._validate_name(name)
        sha = sha.lower()
        self._validate_sha(sha)

        librato.count('blueprint-io-server.bandwidth.in', request.content_length)
        statsd.update('blueprint-io-server.bandwidth.in', request.content_length)

        if hashlib.sha1(request.data).hexdigest() != sha:
            abort(400)

        b = self._blueprint(secret, name)
        if b is None:
            abort(404)
        elif b is False:
            abort(502)
        if '{0}.tar'.format(sha) not in b.sources.itervalues():
            abort(400)

        if not backend.put_tarball(secret, name, sha, request.data):
            abort(502)

        return MeteredResponse(response='',
                             status=202,
                             content_type='text/plain')

    def sh(self, secret, name, name2):
        if 'user-data' == name2:
            return self.user_data(secret, name)
        if name != name2:
            abort(400)
        self._validate_secret(secret)
        self._validate_name(name)

        b = self._blueprint(secret, name)
        if b is None:
            abort(404)
        elif b is False:
            abort(502)
        s = b.sh(server=self._server(), secret=secret)
        s.out.insert(0, '#!/bin/sh\n\n')
        return MeteredResponse(response=s.dumps(),
                             status=200,
                             content_type='text/plain')

    def user_data(self, secret, name):
        self._validate_secret(secret)
        self._validate_name(name)
        b = self._blueprint(secret, name)
        if b is None:
            abort(404)
        elif b is False:
            abort(502)
        return MeteredResponse(response=f"""#!/bin/sh

set -e

TMPDIR="$(mktemp -d)"
cd "$TMPDIR"
trap "rm -rf \\"$TMPDIR\\"" EXIT

wget "{self._server()}/{secret}/{name}/{name}.sh"

sh "$(ls)"
""",
                           status=200,
                           content_type='text/plain')

    def run(self):
        self.app.run(host='0.0.0.0', debug=True)
