from sanic import Sanic
from sanic.response import json
from sanic_openapi import swagger_blueprint, doc
from typing import Optional, List, Dict, Union
from collections import defaultdict
import re

RE_BACKSPACES = re.compile("\b+")

class FamiliaAPI:
    def __init__(self, model_path: str, model_name: str, n_workers: int = None):
        self.app = Sanic("Familia", strict_slashes=True)
        self.app.blueprint(swagger_blueprint)
        self.app.config.API_TITLE = 'Familia API'
        self.app.config.API_DESCRIPTION = 'A Toolkit for Industrial Topic Modeling'
        self.app.config.API_PRODUCES_CONTENT_TYPES = ['application/json']

        self.model_name = model_name.lower()
        self.model_path = model_path
        self.n_workers = n_workers if n_workers else multiprocessing.cpu_count()
        self.model_dir = os.path.join(model_path, model_name)

        self.emb_file = f"{model_name}_twe_lda.model"
        self.inference_engine_lda = InferenceEngineWrapper(self.model_dir, 'lda.conf', self.emb_file)
        self.inference_engine_slda = InferenceEngineWrapper(self.model_dir, 'slda.conf')
        self.twe = TopicalWordEmbeddingsWrapper(self.model_dir, self.emb_file)
        self.lda_topic_words = self._read_topic_words_from_file()

        self._setup_routes()

    def _read_topic_words_from_file(self, topic_words_file_name='topic_words.lda.txt'):
        topic_words = defaultdict(list)
        file_path = os.path.join(self.model_dir, topic_words_file_name)
        if not os.path.exists(file_path):
            return topic_words
        with open(file_path, 'r') as f:
            line = f.readline()
            while line:
                pos = line.find('=')
                line = line[pos + 2:]
                topic_id, num = line.strip().split('\t')
                topic_id, num = int(topic_id), int(num)
                f.readline()
                items = list()
                for i in range(num):
                    data = f.readline()
                    word, score = data.strip().split('\t')
                    items.append([word, float(score)])
                topic_words[topic_id] = items
                line = f.readline()
        return topic_words

    def _get_param(self, request, param_name, default_value=None, is_list=False):
        param_value = (request.form.getlist(param_name) if is_list else request.form.getlist(param_name) or \
                     request.args.get(param_name) or \
                     default_value
        if param_value is None:
            return param_value
        value_type = type(param_value)
        if is_list:
            return param_value if value_type == list else [param_value]
        return param_value[0] if value_type == list else param_value

    def _strip_to_none(self, text: str):
        if text is None:
            return None
        text = text.strip()
        text = re.sub(RE_BACKSPACES, '', text)
        if len(text) == 0:
            return None
        if text == 'None':
            return None
        return text

    def _response(self, success: bool = True, data=None, message=None):
        data = {'success': success, 'message': message, 'data': data}
        data = {k: v for k, v in data.items() if v is not None}
        try:
            return json(data, ensure_ascii=False)
        except Exception:
            data = {'success': success, 'message': traceback.format_exc()}
            return json(data, ensure_ascii=False)

    def _error_response(self, message='Invalid request'):
        return self._response(success=False, message=message)

    def _setup_routes(self):
        @self.app.route('/')
        @doc.description("ping")
        async def api_index(request):
            message = f"Familia API is running, check out the api doc at http://{request.host}/swagger/"
            return self._response(message=message)

        @self.app.route('/tokenize', methods=["POST"])
        @doc.summary("分词")
        async def api_tokenize(request):
            try:
                text = self._get_param(request, 'text')
                if text is None:
                    return self._error_response()
                result = self.inference_engine_lda.tokenize(text)
                return self._response(data=result)
            except Exception as err:
                return self._error_response(str(err))

        @self.app.route('/lda', methods=["POST"])
        @doc.summary("LDA模型推断")
        async def api_lda(request):
            try:
                text = self._get_param(request, 'text')
                n = int(self._get_param(request, 'n', 10))
                if text is None:
                    return self._error_response()
                words = self.inference_engine_lda.tokenize(text)
                result = self.inference_engine_lda.lda_infer(words)
                result = result[:n]
                result = [
                    {
                        'topic_id': topic_id,
                        'score': score,
                        'topic_words': self.twe.nearest_words_around_topic(topic_id),
                        'topic_words_poly': self.lda_topic_words.get(topic_id),
                    } for topic_id, score in result
                ]
                return self._response(data=result)
            except Exception as err:
                return self._error_response(str(err))

    def run(self):
        self.app.run(host='0.0.0.0', port=5000, workers=self.n_workers)
