import re
from http import HTTPStatus
from http.server import BaseHTTPRequestHandler, HTTPServer
from threading import Thread

class HttpServer:
    DEFAULT_RESPONSE = b"Hello!"

    class Handler(BaseHTTPRequestHandler):
        protocol_version = "HTTP/1.1"
        timeout = 3

        STATUS_RE = re.compile(r"/status/(\d+)")

        def do_GET(self):
            status_match = self.STATUS_RE.fullmatch(self.path)
            status = 200
            if status_match:
                status = int(status_match.group(1))
            if status == 200:
                body = HttpServer.DEFAULT_RESPONSE
                self.send_response(HTTPStatus.OK)
                self.send_header("Content-Length", str(len(body)))
                self.end_headers()
                self.wfile.write(body)
            else:
                self.send_error(status)

    def __init__(self):
        self.server = None
        self.server_thread = None

    def create_server(self):
        server_address = ("127.0.0.1", 0)
        return HTTPServer(server_address, self.Handler)

    def run_server(self):
        self.server = self.create_server()
        self.server_thread = Thread(
            target=self.server.serve_forever, daemon=True, name="Test server worker"
        )
        self.server_thread.start()
        return self.server_thread, self.server

    def start(self):
        self.server_thread, self.server = self.run_server()

    def shutdown(self):
        if self.server:
            self.server.shutdown()
        if self.server_thread:
            self.server_thread.join()
