import json
from http.server import BaseHTTPRequestHandler, HTTPServer
from hashlib import sha256
from eth_utils import to_bytes, to_hex

class XUDResolver:
    def __init__(self, host="localhost", port=8000):
        self.host = host
        self.port = port
        self.x_secret = "0x2ff886d47b156de00d4cad5d8c332706692b5b572adfe35e6d2f65e92906806e"
        self.x_secret_hash = to_hex(sha256(to_bytes(hexstr=self.x_secret)).digest())

    def resolve(self, request):
        if "secrethash" not in request:
            return None

        if request["secrethash"] == self.x_secret_hash:
            return {"secret": self.x_secret}
        return None

    def serve(self):
        class SimpleHTTPRequestHandler(BaseHTTPRequestHandler):
            def __init__(self, *args, **kwargs):
                self.resolver = kwargs.pop('resolver')
                super().__init__(*args, **kwargs)

            def do_POST(self):
                try:
                    content_len = int(self.headers.get("Content-Length"))
                    body = self.rfile.read(content_len)

                    preimage = self.resolver.resolve(json.loads(body.decode("utf8")))
                    if preimage is None:
                        self.send_response(404)
                        self.end_headers()
                    else:
                        response = to_bytes(text=json.dumps(preimage))
                        self.send_response(200)
                        self.end_headers()
                        self.wfile.write(response)
                except BaseException:
                    self.send_response(400)
                    self.end_headers()

        httpd = HTTPServer((self.host, self.port), lambda *args, **kwargs: SimpleHTTPRequestHandler(*args, resolver=self, **kwargs))
        httpd.serve_forever()
