from kafka import KafkaProducer
from uuid import uuid4

kafka_settings = {"bootstrap_servers": "kafka:9092"}

class KafkaProducerWrapper:
    def __init__(self) -> None:
        self._producer = KafkaProducer(
            bootstrap_servers=kafka_settings["bootstrap_servers"]
        )
        self._topic = f"integration-tests-{uuid4()}"

    def send(self, message: str | tuple[str, str]) -> None:
        if isinstance(message, tuple):
            (key, value) = message
        else:
            (key, value) = str(uuid4()), message
        self._producer.send(self._topic, key=key.encode(), value=value.encode())

    def fill(self, messages: Iterable[str | tuple[str, str]]) -> None:
        for msg in messages:
            self.send(msg)
        self._producer.flush()

    def close(self) -> None:
        self._producer.close()

    @property
    def topic(self) -> str:
        return self._topic
