import paho.mqtt.client as mqtt

class MQTTConsumer:
    def __init__(self, client_id=None, host="mqtt.eclipseprojects.io", port=1883, keepalive=60, username=None, password=None):
        self.final_mid = 0
        self.client = mqtt.Client(mqtt.CallbackAPIVersion.VERSION2, client_id)
        self.client._userdata = False
        self.client.on_connect = self.on_connect
        self.client.on_message = self.on_message
        self.client.on_publish = self.on_publish

        if username:
            self.client.username_pw_set(username, password)
        self.client.connect(host, port, keepalive)

    def on_connect(self, mqttc, userdata, flags, reason_code, properties):
        if userdata:
            print(f"reason_code: {reason_code}")

    def on_message(self, mqttc, userdata, msg):
        if msg.retain == 0:
            pass
        else:
            if userdata:
                print("Clearing topic " + msg.topic)
            (rc, self.final_mid) = mqttc.publish(msg.topic, None, 1, True)

    def on_publish(self, mqttc, userdata, mid, reason_code, properties):
        if mid == self.final_mid:
            sys.exit()

    def subscribe(self, topic):
        self.client.subscribe(topic)

    def start_consuming(self):
        self.client.loop_forever()
