import argparse
import os
import ssl
import time
import paho.mqtt.client as mqtt

def mqtt_publisher(host, topic, qos, clientid, username, disable_clean_session, password, port, nummsgs, delay, keepalive, use_tls, insecure, cacerts, tls_version, debug):
    usetls = use_tls
    if cacerts:
        usetls = True

    if port is None:
        if usetls:
            port = 8883
        else:
            port = 1883

    mqttc = mqtt.Client(mqtt.CallbackAPIVersion.VERSION2, clientid, clean_session = not disable_clean_session)

    if usetls:
        if tls_version == "tlsv1.2":
            tlsVersion = ssl.PROTOCOL_TLSv1_2
        elif tls_version == "tlsv1.1":
            tlsVersion = ssl.PROTOCOL_TLSv1_1
        elif tls_version == "tlsv1":
            tlsVersion = ssl.PROTOCOL_TLSv1
        elif tls_version is None:
            tlsVersion = None
        else:
            print("Unknown TLS version - ignoring")
            tlsVersion = None

        if not insecure:
            cert_required = ssl.CERT_REQUIRED
        else:
            cert_required = ssl.CERT_NONE

        mqttc.tls_set(ca_certs=cacerts, certfile=None, keyfile=None, cert_reqs=cert_required, tls_version=tlsVersion)

        if insecure:
            mqttc.tls_insecure_set(True)

    if username or password:
        mqttc.username_pw_set(username, password)

    mqttc.on_message = on_message
    mqttc.on_connect = on_connect
    mqttc.on_publish = on_publish

    if debug:
        mqttc.on_log = on_log

    print("Connecting to "+host+" port: "+str(port))
    mqttc.connect(host, port, keepalive)

    mqttc.loop_start()

    for x in range (0, nummsgs):
        msg_txt = '{"msgnum": "'+str(x)+'"}'
        print("Publishing: "+msg_txt)
        infot = mqttc.publish(topic, msg_txt, qos=qos)
        infot.wait_for_publish()

        time.sleep(delay)

    mqttc.disconnect()
