import socket
import re

class ZookeeperTCPClient:
    def __init__(self):
        pass

    def get_raw_stats(self, host, port):
        data = ''
        try:
            if port is None:
                sock = socket.socket(socket.AF_UNIX, socket.SOCK_STREAM)
                sock.connect(host)
            else:
                sock = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
                sock.connect((host, int(port)))
            sock.send('mntr\n')
            data = sock.recv(4096)
        except socket.error:
            raise Exception('Failed to get stats from %s:%s' % (host, port))
        finally:
            sock.close()
        return data

    def get_stats(self, host, port):
        ignored = ('zk_version', 'zk_server_state')
        pid = None
        stats = {}
        data = self.get_raw_stats(host, port)

        for line in data.splitlines():
            pieces = line.split()
            if pieces[0] in ignored:
                continue
            stats[pieces[0]] = pieces[1]

        try:
            cmdline = "/proc/%s/cmdline" % pid
            f = open(cmdline, 'r')
            m = re.search("-c\x00(\d+)", f.readline())
            if m is not None:
                stats['limit_maxconn'] = m.group(1)
            f.close()
        except:
            pass

        return stats
