import os
import sys
import time
import errno
import socket
import select
import logging
import threading

class EventHandler:
    def __init__(self, address: tuple, sock: socket.socket=None) -> None:
        self._write_lock = threading.RLock()
        self._read_lock = threading.RLock()
        self.address = address
        self.outbuffer = b''
        self.inbuffer = b''
        self.sock = sock if sock is not None else socket.socket(socket.AF_INET, socket.SOCK_STREAM)
        self.sock.connect(address)
        self.connected = True
        self.sock.setblocking(False)

    def fileno(self) -> int:
        return self.sock.fileno()

    def send(self) -> int:
        with self._write_lock:
            while len(self.outbuffer) > 0:
                try:
                    sent = self.sock.send(self.outbuffer)
                    self.outbuffer = self.outbuffer[sent:]
                except socket.error as error:
                    if error.args[0] == errno.EAGAIN:
                        time.sleep(0.1)
                    elif error.args[0] in (errno.ECONNRESET, errno.ENOTCONN, errno.ESHUTDOWN, errno.ECONNABORTED, errno.EPIPE):
                        self.close()
                        return 0
                    elif os.name == 'posix':
                        if sys.platform == 'darwin' and error.args[0] == errno.EBADF:
                            self.close()
                            return 0
                        elif error.args[0] == errno.EBADFD:
                            self.close()
                            return 0
                        raise
                    else:
                        raise

    def recv(self) -> None:
        try:
            data = self.sock.recv(4096)
        except socket.error as error:
            if error.args[0] in (errno.EWOULDBLOCK, errno.EAGAIN):
                return None
            elif error.args[0] == errno.ECONNRESET:
                self.close()
                return None
            else:
                raise

        if not data:
            self.close()
            return None

        self.inbuffer += data

        while self.inbuffer:
            match = b'\r\n'
            index = self.inbuffer.find(match)
            if index != -1:
                if index > 0:
                    self.handle_read(self.inbuffer[:index])
                self.inbuffer = self.inbuffer[index+len(match):]
                self.process_message()
            else:
                index = len(match) - 1
                while index and not self.inbuffer.endswith(match[:index]):
                    index -= 1
                if index:
                    if index != len(self.inbuffer):
                        self.handle_read(self.inbuffer[:-index])
                        self.inbuffer = self.inbuffer[-index:]
                    break
                else:
                    self.handle_read(self.inbuffer)
                    self.inbuffer = b''

    def push(self, data: bytes) -> None:
        self.outbuffer += data

    def handle_read(self, data: bytes) -> None:
        raise NotImplementedError('You have to implement this method')

    def process_message(self) -> None:
        raise NotImplementedError('You have to implement this method')

    def ready_to_read(self) -> bool:
        return True

    def ready_to_write(self) -> bool:
        return True

    def close(self) -> None:
        self.sock.close()
        self.connected = False
