import os
import json
import yaml
import httpx
import asyncio
from typing import Union

class TokenManager:
    def __init__(self):
        path = os.path.abspath(os.path.dirname(__file__))
        with open(f"{path}/config.yaml", "r", encoding="utf-8") as f:
            config = yaml.safe_load(f)

        tiktok_manager = config.get("TokenManager").get("tiktok")
        self.token_conf = tiktok_manager.get("msToken", None)
        self.ttwid_conf = tiktok_manager.get("ttwid", None)
        self.odin_tt_conf = tiktok_manager.get("odin_tt", None)
        proxies_conf = tiktok_manager.get("proxies", None)
        self.proxies = {
            "http://": proxies_conf.get("http", None),
            "https://": proxies_conf.get("https", None),
        }

    def gen_real_msToken(self) -> str:
        payload = json.dumps({
            "magic": self.token_conf["magic"],
            "version": self.token_conf["version"],
            "dataType": self.token_conf["dataType"],
            "strData": self.token_conf["strData"],
            "tspFromClient": get_timestamp(),
        })

        headers = {
            "User-Agent": self.token_conf["User-Agent"],
            "Content-Type": "application/json",
        }

        transport = httpx.HTTPTransport(retries=5)
        with httpx.Client(transport=transport, proxies=self.proxies) as client:
            try:
                response = client.post(
                    self.token_conf["url"], headers=headers, content=payload
                )
                response.raise_for_status()
                msToken = str(httpx.Cookies(response.cookies).get("msToken"))
                return msToken
            except Exception as e:
                logger.error(f"生成TikTok msToken API错误：{e}")
                return self.gen_false_msToken()

    def gen_false_msToken(self) -> str:
        return gen_random_str(146) + "=="

    def gen_ttwid(self, cookie: str) -> str:
        transport = httpx.HTTPTransport(retries=5)
        with httpx.Client(transport=transport, proxies=self.proxies) as client:
            try:
                response = client.post(
                    self.ttwid_conf["url"],
                    content=self.ttwid_conf["data"],
                    headers={
                        "Cookie": cookie,
                        "Content-Type": "text/plain",
                    },
                )
                response.raise_for_status()
                ttwid = httpx.Cookies(response.cookies).get("ttwid")
                if ttwid is None:
                    raise APIResponseError("ttwid: 检查没有通过, 请更新配置文件中的ttwid")
                return ttwid
            except httpx.RequestError as exc:
                raise APIConnectionError(f"请求端点失败，请检查当前网络环境。链接：{self.ttwid_conf['url']}，代理：{self.proxies}，异常类名：{self.__class__.__name__}，异常详细信息：{exc}")

    def gen_odin_tt(self) -> str:
        transport = httpx.HTTPTransport(retries=5)
        with httpx.Client(transport=transport, proxies=self.proxies) as client:
            try:
                response = client.get(self.odin_tt_conf["url"])
                response.raise_for_status()
                odin_tt = httpx.Cookies(response.cookies).get("odin_tt")
                if odin_tt is None:
                    raise APIResponseError(f"{'odin_tt'} 内容不符合要求")
                return odin_tt
            except httpx.RequestError as exc:
                raise APIConnectionError(f"请求端点失败，请检查当前网络环境。链接：{self.odin_tt_conf['url']}，代理：{self.proxies}，异常类名：{self.__class__.__name__}，异常详细信息：{exc}")
