import socket
from typing import Optional, Union

class TLSServerAutomaton:
    def __init__(self, server="127.0.0.1", sport=4433,
                 mycert=None, mykey=None,
                 preferred_ciphersuite: Optional[int] = None,
                 preferred_signature_algorithm: Union[str, int, None] = None,
                 client_auth=False,
                 is_echo_server=True,
                 max_client_idle_time=60,
                 handle_session_ticket=None,
                 session_ticket_file=None,
                 curve=None,
                 cookie=False,
                 psk=None,
                 psk_mode=None):

        self.serversocket = None
        self.ip_family = socket.AF_INET
        self.local_ip = server
        self.local_port = sport
        self.remote_ip = None
        self.remote_port = None

        self.client_auth = client_auth
        self.is_echo_server = is_echo_server
        self.max_client_idle_time = max_client_idle_time
        self.curve = curve
        self.preferred_ciphersuite = preferred_ciphersuite
        self.preferred_signature_algorithm = preferred_signature_algorithm
        self.cookie = cookie
        self.psk_secret = psk
        self.psk_mode = psk_mode

    def bind_socket(self):
        s = socket.socket(self.ip_family, socket.SOCK_STREAM)
        self.serversocket = s
        s.setsockopt(socket.SOL_SOCKET, socket.SO_REUSEADDR, 1)
        try:
            s.bind((self.local_ip, self.local_port))
            s.listen(1)
            return True
        except Exception as e:
            print(f"Unable to bind on {self.local_ip}:{self.local_port}! ({e})")
            return False

    def accept_client(self):
        self.socket, addr = self.serversocket.accept()
        if not isinstance(addr, tuple):
            addr = self.socket.getpeername()
        if len(addr) > 2:
            addr = (addr[0], addr[1])
        self.remote_ip, self.remote_port = addr
        print(f"Accepted connection from {self.remote_ip}:{self.remote_port}")

    def close_client_socket(self):
        self.socket.close()

    def close_server_socket(self):
        if self.serversocket:
            self.serversocket.close()

    def receive_data(self, timeout=None):
        if timeout:
            self.socket.settimeout(timeout)
        try:
            data = self.socket.recv(4096)
            return data if data else None
        except socket.timeout:
            return None
        except Exception:
            return None

    def send_data(self, data):
        try:
            self.socket.sendall(data)
            return True
        except Exception:
            return False

    def run_server(self):
        if not self.bind_socket():
            return

        try:
            while True:
                self.accept_client()
                try:
                    while True:
                        data = self.receive_data(self.max_client_idle_time)
                        if not data:
                            break
                        if self.is_echo_server:
                            self.send_data(data)
                finally:
                    self.close_client_socket()
        finally:
            self.close_server_socket()
