import ctypes
import json
import socketserver
import threading

class ReceiveMsgSocketServer(socketserver.BaseRequestHandler):
    def __init__(self, *args, **kwargs):
        super().__init__(*args, **kwargs)

    def handle(self):
        conn = self.request
        while True:
            try:
                ptr_data = b""
                while True:
                    data = conn.recv(1024)
                    ptr_data += data
                    if len(data) == 0 or data[-1] == 0xA:
                        break
                msg = json.loads(ptr_data.decode('utf-8'))
                ReceiveMsgSocketServer.msg_callback(msg)
            except OSError:
                break
            except json.JSONDecodeError:
                pass
            conn.sendall("200 OK".encode())
        conn.close()

    @staticmethod
    def msg_callback(msg):
        print(msg)

class SocketServerManager:
    def __init__(self, port: int = 10808, request_handler=ReceiveMsgSocketServer):
        self.port = port
        self.request_handler = request_handler
        self.server = None
        self.server_thread = None

    def start_server(self, main_thread: bool = True) -> int or None:
        ip_port = ("127.0.0.1", self.port)
        try:
            self.server = socketserver.ThreadingTCPServer(ip_port, self.request_handler)
            if main_thread:
                self.server.serve_forever()
            else:
                self.server_thread = threading.Thread(target=self.server.serve_forever)
                self.server_thread.setDaemon(True)
                self.server_thread.start()
                return self.server_thread.ident
        except KeyboardInterrupt:
            pass
        except Exception as e:
            print(e)
        return None

    def stop_server(self):
        if self.server:
            self.server.shutdown()
        if self.server_thread:
            self.server_thread.join()
