import httpx
import typing
from datetime import timedelta

async def make_http_request(url: str, method: str = "GET", content: typing.Optional[bytes] = None, json: typing.Optional[dict] = None, headers: typing.Optional[dict] = None) -> httpx.Response:
    async with httpx.AsyncClient() as client:
        if method == "GET":
            response = await client.get(url)
        elif method == "POST":
            if content:
                response = await client.post(url, content=content)
            elif json:
                response = await client.post(url, json=json)
        elif method == "PUT":
            response = await client.put(url, content=content)
        elif method == "DELETE":
            response = await client.delete(url)
        elif method == "PATCH":
            response = await client.patch(url, content=content)
        elif method == "HEAD":
            response = await client.head(url)
        elif method == "OPTIONS":
            response = await client.options(url)

        return response
