import threading
from time import time
from functools import lru_cache
from typing import Any
import zmq

class RpcClient:
    def __init__(self) -> None:
        self._context: zmq.Context = zmq.Context()
        self._socket_sub: zmq.Socket = self._context.socket(zmq.SUB)

        self._socket_sub.setsockopt(zmq.TCP_KEEPALIVE, 1)
        self._socket_sub.setsockopt(zmq.TCP_KEEPALIVE_IDLE, 60)

        self._active: bool = False
        self._thread: threading.Thread = None
        self._lock: threading.Lock = threading.Lock()
        self._last_received_ping: time = time()

    def start(self, sub_address: str) -> None:
        if self._active:
            return

        self._socket_sub.connect(sub_address)
        self._active = True
        self._thread = threading.Thread(target=self.run)
        self._thread.start()
        self._last_received_ping = time()

    def stop(self) -> None:
        if not self._active:
            return
        self._active = False

    def join(self) -> None:
        if self._thread and self._thread.is_alive():
            self._thread.join()
        self._thread = None

    def run(self) -> None:
        pull_tolerance: int = HEARTBEAT_TOLERANCE * 1000

        while self._active:
            if not self._socket_sub.poll(pull_tolerance):
                self.on_disconnected()
                continue

            topic, data = self._socket_sub.recv_pyobj(flags=zmq.NOBLOCK)

            if topic == HEARTBEAT_TOPIC:
                self._last_received_ping = data
            else:
                self.callback(topic, data)

        self._socket_sub.close()

    def callback(self, topic: str, data: Any) -> None:
        raise NotImplementedError

    def subscribe_topic(self, topic: str) -> None:
        self._socket_sub.setsockopt_string(zmq.SUBSCRIBE, topic)

    def on_disconnected(self):
        msg: str = f"RpcServer has no response over {HEARTBEAT_TOLERANCE} seconds, please check you connection."
        print(msg)
