import base64
import hashlib
import hmac
import json
from typing import Any, Dict, Optional
import httpx
from pydantic.v1 import BaseModel

class ExternalActionResponse(BaseModel):
    result: dict
    success: bool
    agent_message: Optional[str] = None

class ExternalActionsRequester:
    def __init__(self, url: str) -> None:
        self.url = url

    async def send_request(
        self,
        payload: Dict[str, Any],
        signature_secret: str,
        additional_payload_values: Dict[str, Any] = {},
        additional_headers: Dict[str, str] = {},
        transport: httpx.AsyncHTTPTransport = httpx.AsyncHTTPTransport(retries=2),
    ) -> ExternalActionResponse:
        encoded_payload = json.dumps({"payload": payload} | additional_payload_values).encode("utf-8")
        headers = {
            "Accept": "application/json",
            "Content-Type": "application/json",
            "x-vocode-signature": self._encode_payload(encoded_payload, signature_secret),
            **additional_headers,
        }

        async with httpx.AsyncClient(
            headers=headers,
            transport=transport,
            timeout=10,
        ) as client:
            try:
                response = await client.post(self.url, content=encoded_payload)
                response.raise_for_status()
                data = response.json()
                return self._validate_response(data)
            except httpx.HTTPStatusError as e:
                if e.response.status_code == 401:
                    return ExternalActionResponse(result={"info": "Unauthorized"}, success=False)
                elif e.response.status_code == 403:
                    return ExternalActionResponse(result={"info": "Forbidden"}, success=False)
                elif 400 <= e.response.status_code < 500:
                    return ExternalActionResponse(result={"info": "Client error"}, success=False)
                elif e.response.status_code >= 500:
                    return ExternalActionResponse(result={"info": "Server error"}, success=False)
                else:
                    raise e

    def _encode_payload(self, payload: bytes, signature_secret: str) -> str:
        signature_as_bytes = base64.b64decode(signature_secret)
        digest = hmac.new(signature_as_bytes, payload, hashlib.sha256).digest()
        return base64.b64encode(digest).decode()

    def _validate_response(self, response: Dict[str, Any]) -> ExternalActionResponse:
        if "result" not in response:
            raise ValueError("Invalid response format: missing 'result'")
        if not isinstance(response["result"], dict):
            raise ValueError("Invalid response format: 'result' must be a dict")
        if "agent_message" in response and not isinstance(response["agent_message"], str):
            raise ValueError("Invalid response format: 'agent_message' must be a string")
        return ExternalActionResponse(
            result=response["result"],
            agent_message=response.get("agent_message"),
            success=True,
        )
