#include <chrono>
#include <cstring>
#include <stdexcept>
#include <string>
#include <sys/socket.h>
#include <sys/types.h>
#include <unistd.h>
#include <arpa/inet.h>
#include <netinet/in.h>
#include <netdb.h>
#include <fcntl.h>
#include <errno.h>

class TCPClient {
public:
    TCPClient(const std::string& address, u16 port)
        : address(address), port(port), main_socket(-1), connected(false) {}

    ~TCPClient() {
        Disconnect();
    }

    bool Connect() {
        struct addrinfo hints, *res;
        memset(&hints, 0, sizeof hints);
        hints.ai_family = AF_UNSPEC;
        hints.ai_socktype = SOCK_STREAM;

        int status = getaddrinfo(address.c_str(), std::to_string(port).c_str(), &hints, &res);
        if (status != 0) {
            throw std::runtime_error(std::string("getaddrinfo error: ") + gai_strerror(status));
        }

        main_socket = socket(res->ai_family, res->ai_socktype, res->ai_protocol);
        if (main_socket == -1) {
            freeaddrinfo(res);
            throw std::runtime_error(std::string("socket error: ") + strerror(errno));
        }

        if (!SetNonBlock(main_socket, true)) {
            freeaddrinfo(res);
            throw std::runtime_error("SetNonBlock error");
        }

        status = connect(main_socket, res->ai_addr, res->ai_addrlen);
        if (status == -1 && errno != EINPROGRESS) {
            freeaddrinfo(res);
            throw std::runtime_error(std::string("connect error: ") + strerror(errno));
        }

        freeaddrinfo(res);

        fd_set writefds;
        FD_ZERO(&writefds);
        FD_SET(main_socket, &writefds);

        struct timeval tv;
        tv.tv_sec = 5;
        tv.tv_usec = 0;

        status = select(main_socket + 1, nullptr, &writefds, nullptr, &tv);
        if (status == -1) {
            throw std::runtime_error(std::string("select error: ") + strerror(errno));
        } else if (status == 0) {
            throw std::runtime_error("Connection timed out");
        }

        int error = 0;
        socklen_t len = sizeof(error);
        status = getsockopt(main_socket, SOL_SOCKET, SO_ERROR, &error, &len);
        if (status == -1) {
            throw std::runtime_error(std::string("getsockopt error: ") + strerror(errno));
        }

        if (error != 0) {
            throw std::runtime_error(std::string("Connection error: ") + strerror(error));
        }

        connected = true;
        return true;
    }

    bool Read(void* buffer, size_t size, const std::chrono::nanoseconds& timeout) {
        if (!connected) {
            throw std::runtime_error("Not connected to server");
        }

        fd_set readfds;
        FD_ZERO(&readfds);
        FD_SET(main_socket, &readfds);

        struct timeval tv;
        tv.tv_sec = std::chrono::duration_cast<std::chrono::seconds>(timeout).count();
        tv.tv_usec = std::chrono::duration_cast<std::chrono::microseconds>(timeout).count() % 1000000;

        int status = select(main_socket + 1, &readfds, nullptr, nullptr, &tv);
        if (status == -1) {
            throw std::runtime_error(std::string("select error: ") + strerror(errno));
        } else if (status == 0) {
            throw std::runtime_error("Read timed out");
        }

        ssize_t bytes_read = recv(main_socket, buffer, size, 0);
        if (bytes_read == -1) {
            throw std::runtime_error(std::string("recv error: ") + strerror(errno));
        } else if (bytes_read == 0) {
            connected = false;
            return false;
        }

        return true;
    }

    bool Write(const void* buffer, size_t size, const std::chrono::nanoseconds& timeout) {
        if (!connected) {
            throw std::runtime_error("Not connected to server");
        }

        fd_set writefds;
        FD_ZERO(&writefds);
        FD_SET(main_socket, &writefds);

        struct timeval tv;
        tv.tv_sec = std::chrono::duration_cast<std::chrono::seconds>(timeout).count();
        tv.tv_usec = std::chrono::duration_cast<std::chrono::microseconds>(timeout).count() % 1000000;

        int status = select(main_socket + 1, nullptr, &writefds, nullptr, &tv);
        if (status == -1) {
            throw std::runtime_error(std::string("select error: ") + strerror(errno));
        } else if (status == 0) {
            throw std::runtime_error("Write timed out");
        }

        ssize_t bytes_written = send(main_socket, buffer, size, 0);
        if (bytes_written == -1) {
            throw std::runtime_error(std::string("send error: ") + strerror(errno));
        } else if (bytes_written != size) {
            throw std::runtime_error("Incomplete write");
        }

        return true;
    }

    void Disconnect() {
        if (connected) {
            close(main_socket);
            connected = false;
        }
    }

private:
    bool SetNonBlock(SocketHolder sockFD, bool nonBlocking) {
        int flags = fcntl(sockFD, F_GETFL, 0);
        if (flags == -1) {
            return false;
        }

        if (nonBlocking) {
            flags |= O_NONBLOCK;
        } else {
            flags &= ~O_NONBLOCK;
        }

        if (fcntl(sockFD, F_SETFL, flags) == -1) {
            return false;
        }

        return true;
    }

    bool ConnectWithTimeout(SocketHolder sockFD, void* server_addr, size_t server_addr_len, int timeout_seconds) {
        struct timeval tv;
        tv.tv_sec = timeout_seconds;
        tv.tv_usec = 0;

        setsockopt(sockFD, SOL_SOCKET, SO_RCVTIMEO, (const char*)&tv, sizeof tv);
        setsockopt(sockFD, SOL_SOCKET, SO_SNDTIMEO, (const char*)&tv, sizeof tv);

        int status = connect(sockFD, server_addr, server_addr_len);
        if (status == -1 && errno != EINPROGRESS) {
            return false;
        }

        fd_set writefds;
        FD_ZERO(&writefds);
        FD_SET(sockFD, &writefds);

        status = select(sockFD + 1, nullptr, &writefds, nullptr, &tv);
        if (status == -1) {
            return false;
        } else if (status == 0) {
            return false;
        }

        int error = 0;
        socklen_t len = sizeof(error);
        status = getsockopt(sockFD, SOL_SOCKET, SO_ERROR, &error, &len);
        if (status == -1) {
            return false;
        }

        if (error != 0) {
            return false;
        }

        return true;
    }

    std::string address;
    u16 port;
    SocketHolder main_socket;
    bool connected;
};
