class CurlHttpClient {
public:
    struct MemoryStruct {
        char* memory;
        size_t size;
    };

    static size_t WriteMemoryCallback(void* contents, size_t size, size_t nmemb, void* userp) {
        size_t realsize = size * nmemb;
        struct MemoryStruct* mem = (struct MemoryStruct*)userp;

        char* ptr = (char*)realloc(mem->memory, mem->size + realsize + 1);
        if (ptr == NULL) {
            // out of memory!
            printf("not enough memory (realloc returned NULL)\n");
            return 0;
        }

        mem->memory = ptr;
        memcpy(&(mem->memory[mem->size]), contents, realsize);
        mem->size += realsize;
        mem->memory[mem->size] = 0;

        return realsize;
    }

    long downloadPage(const std::string& url, std::string& res, const std::vector<std::string>& headers, const std::string& body) {
        CURL* curl;
        CURLcode resCurl;
        long httpCode = 0;

        curl_global_init(CURL_GLOBAL_DEFAULT);
        curl = curl_easy_init();
        if (curl) {
            struct curl_slist* headerList = NULL;
            for (const auto& header : headers) {
                headerList = curl_slist_append(headerList, header.c_str());
            }

            curl_easy_setopt(curl, CURLOPT_URL, url.c_str());
            curl_easy_setopt(curl, CURLOPT_HTTPHEADER, headerList);
            curl_easy_setopt(curl, CURLOPT_POSTFIELDS, body.c_str());
            curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, WriteMemoryCallback);
            curl_easy_setopt(curl, CURLOPT_WRITEDATA, (void*)&memoryStruct);

            resCurl = curl_easy_perform(curl);

            if (resCurl != CURLE_OK) {
                fprintf(stderr, "curl_easy_perform() failed: %s\n", curl_easy_strerror(resCurl));
            } else {
                curl_easy_getinfo(curl, CURLINFO_RESPONSE_CODE, &httpCode);
                res = memoryStruct.memory;
            }

            curl_easy_cleanup(curl);
            curl_slist_free_all(headerList);
        }

        curl_global_cleanup();
        return httpCode;
    }

    long getRequest(const std::string& url, json& res, const std::vector<std::string>& headers, const std::string& body) {
        std::string response;
        long httpCode = downloadPage(url, response, headers, body);
        if (httpCode == 200) {
            res = json::parse(response);
        }
        return httpCode;
    }

private:
    MemoryStruct memoryStruct;
};
