    UDPRecv(DFNetWork* poDFNetWork) : m_poDFNetWork(poDFNetWork), m_iSockFD(-1), m_bIsEnd(false), m_bIsStarted(false) {}

    ~UDPRecv() {
        if (m_iSockFD != -1) {
            close(m_iSockFD);
        }
    }

    void Stop() {
        m_bIsEnd = true;
        // Wait for the receiver to finish
    }

    int Init(int iPort) {
        m_iSockFD = socket(AF_INET, SOCK_DGRAM, 0);
        if (m_iSockFD == -1) {
            return -1;
        }

        int opt = 1;
        if (setsockopt(m_iSockFD, SOL_SOCKET, SO_REUSEADDR, &opt, sizeof(opt)) < 0) {
            close(m_iSockFD);
            return -1;
        }

        struct sockaddr_in addr;
        memset(&addr, 0, sizeof(addr));
        addr.sin_family = AF_INET;
        addr.sin_addr.s_addr = htonl(INADDR_ANY);
        addr.sin_port = htons(iPort);

        if (bind(m_iSockFD, (struct sockaddr*)&addr, sizeof(addr)) < 0) {
            close(m_iSockFD);
            return -1;
        }

        return 0;
    }

    void run() {
        m_bIsStarted = true;
        struct pollfd fds[1];
        fds[0].fd = m_iSockFD;
        fds[0].events = POLLIN;

        char buffer[1024];
        while (!m_bIsEnd) {
            int ret = poll(fds, 1, 1000);
            if (ret > 0) {
                if (fds[0].revents & POLLIN) {
                    struct sockaddr_in addr;
                    socklen_t addrLen = sizeof(addr);
                    int length = recvfrom(m_iSockFD, buffer, sizeof(buffer), 0, (struct sockaddr*)&addr, &addrLen);
                    if (length > 0) {
                        m_poDFNetWork->ProcessMessage(buffer, length);
                    }
                }
            }
        }
        m_bIsStarted = false;
    }

private:
    DFNetWork* m_poDFNetWork;
    int m_iSockFD;
    bool m_bIsEnd;
    bool m_bIsStarted;
};
