class DownloadTask {
public:
    DownloadTask(const std::string& url, const std::string& output, const std::string& defaultFile, time_t mtime, const std::string& name = "") :
        m_url(url), m_urlBackup(""), m_output(output), m_outputTmp(output + ".tmp"), m_default(defaultFile), m_mtime(mtime), m_retries(0), m_name(name), m_userAgent("libcurl-agent/1.0"), m_error("") {}

    static void initialize() {
        curl_global_init(CURL_GLOBAL_ALL);
    }

    static void uninitialize() {
        curl_global_cleanup();
    }

    bool run() {
        if (downloadFile(m_url)) {
            return true;
        }
        if (!m_urlBackup.empty() && downloadFile(m_urlBackup)) {
            return true;
        }
        std::ofstream defaultFile(m_default, std::ios::binary);
        if (defaultFile) {
            defaultFile.close();
            return true;
        }
        m_error = "Failed to download file and create default file";
        return false;
    }

    unsigned int getRetries() const {
        return m_retries;
    }

private:
    bool downloadFile(const std::string& url) {
        CURL* curl = curl_easy_init();
        if (!curl) {
            m_error = "Failed to initialize libcurl";
            return false;
        }

        std::ofstream file(m_outputTmp, std::ios::binary);
        if (!file) {
            m_error = "Failed to open temporary file";
            curl_easy_cleanup(curl);
            return false;
        }

        curl_easy_setopt(curl, CURLOPT_URL, url.c_str());
        curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, &DownloadTask::writeData);
        curl_easy_setopt(curl, CURLOPT_WRITEDATA, &file);
        curl_easy_setopt(curl, CURLOPT_USERAGENT, m_userAgent.c_str());

        CURLcode res = curl_easy_perform(curl);
        if (res != CURLE_OK) {
            m_error = std::string("Failed to download file: ") + curl_easy_strerror(res);
            file.close();
            curl_easy_cleanup(curl);
            return false;
        }

        file.close();
        curl_easy_cleanup(curl);

        if (m_mtime != 0) {
            std::time_t now = std::time(nullptr);
            if (now != (std::time_t)-1) {
                std::tm* timeinfo = std::localtime(&now);
                if (timeinfo) {
                    timeinfo->tm_sec = 0;
                    timeinfo->tm_min = 0;
                    timeinfo->tm_hour = 0;
                    timeinfo->tm_mday = m_mtime;
                    timeinfo->tm_mon = 0;
                    timeinfo->tm_year = m_mtime - 1;
                    std::mktime(timeinfo);
                    std::ofstream::pos_type pos = file.tellp();
                    file.seekp(pos);
                    file.write(reinterpret_cast<char*>(&now), sizeof(now));
                }
            }
        }

        if (std::rename(m_outputTmp.c_str(), m_output.c_str()) != 0) {
            m_error = "Failed to rename temporary file";
            return false;
        }

        return true;
    }

    static size_t writeData(void* ptr, size_t size, size_t nmemb, std::ofstream* file) {
        file->write(static_cast<char*>(ptr), size * nmemb);
        return size * nmemb;
    }

    std::string m_url;
    std::string m_urlBackup;
    std::string m_output;
    std::string m_outputTmp;
    std::string m_default;
    time_t m_mtime;
    unsigned int m_retries;
    std::string m_name;
    std::string m_userAgent;
    std::string m_error;
};
