        curl_easy_setopt(curl, CURLOPT_HTTPHEADER, http_headers);
        curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, &callback);
        curl_easy_setopt(curl, CURLOPT_WRITEDATA, (void *) resp);
