class Task {
public:
    Task(const std::string& url, const std::string& output) : m_url(url), m_output(output) {}

    void run() {
        CURL* curl = curl_easy_init();
        if (curl) {
            std::ofstream file(m_output, std::ios::binary);
            if (file.is_open()) {
                curl_easy_setopt(curl, CURLOPT_URL, m_url.c_str());
                curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, writeData);
                curl_easy_setopt(curl, CURLOPT_WRITEDATA, &file);
                CURLcode res = curl_easy_perform(curl);
                if (res != CURLE_OK) {
                    std::cerr << "Failed to download " << m_url << ": " << curl_easy_strerror(res) << std::endl;
                }
                file.close();
            } else {
                std::cerr << "Failed to open output file " << m_output << std::endl;
            }
            curl_easy_cleanup(curl);
        } else {
            std::cerr << "Failed to initialize libcurl" << std::endl;
        }
    }

private:
    std::string m_url;
    std::string m_output;

    static size_t writeData(void* buffer, size_t size, size_t nmemb, void* userp) {
        std::ofstream* file = static_cast<std::ofstream*>(userp);
        file->write(static_cast<char*>(buffer), size * nmemb);
        return size * nmemb;
    }
};

class DownloadPool {
public:
    DownloadPool() {
        curl_global_init(CURL_GLOBAL_DEFAULT);
        for (int i = 0; i < std::thread::hardware_concurrency(); ++i) {
            m_threads.emplace_back(&DownloadPool::run, this);
        }
    }

    ~DownloadPool() {
        setNoMoreTask();
        finishAndWaitForExit();
        curl_global_cleanup();
    }

    void addTask(const std::string& url, const std::string& output) {
        std::lock_guard<std::mutex> lock(m_mtx);
        if (m_urls.find(url) == m_urls.end()) {
            m_urls.insert(url);
            m_queue.emplace(url, output);
        }
    }

    void setNoMoreTask() {
        std::lock_guard<std::mutex> lock(m_mtx);
        m_noMoreTask = true;
    }

    void finishAndWaitForExit() {
        for (std::thread& thread : m_threads) {
            if (thread.joinable()) {
                thread.join();
            }
        }
    }

private:
    void run() {
        while (true) {
            std::string url;
            std::string output;
            {
                std::lock_guard<std::mutex> lock(m_mtx);
                if (m_queue.empty() && m_noMoreTask) {
                    break;
                }
                if (!m_queue.empty()) {
                    Task task = m_queue.front();
                    m_queue.pop();
                    url = task.m_url;
                    output = task.m_output;
                }
            }
            if (!url.empty()) {
                Task task(url, output);
                task.run();
            }
        }
    }

    std::vector<std::thread> m_threads;
    std::queue<Task> m_queue;
    std::unordered_set<std::string> m_urls;
    std::mutex m_mtx;
    bool m_noMoreTask = false;
};
