#include <fcntl.h>
#include <cstring>
#include <string>
#include <vector>
#include <chrono>
#include <system_error>
#include <memory>

class LinHttpHandler {
public:
    class SocketCloser {
    public:
        explicit SocketCloser(int sockFd) : fd(sockFd) {}
        ~SocketCloser() { if(fd != -1) close(fd); }
        void release() { fd = -1; }
    private:
        int fd;
    };

    std::string send(const std::string& msg, const std::string& adr, int port) {
        addrinfo hints{}, *res;
        hints.ai_family = AF_UNSPEC;
        hints.ai_socktype = SOCK_STREAM;

        int status = getaddrinfo(adr.c_str(), std::to_string(port).c_str(), &hints, &res);
        if(status != 0) throw std::system_error(errno, std::system_category(), "getaddrinfo");
        std::unique_ptr<addrinfo, decltype(&freeaddrinfo)> ai(res, freeaddrinfo);

        int sockfd = -1;
        for(auto p = res; p; p = p->ai_next) {
            sockfd = socket(p->ai_family, p->ai_socktype, p->ai_protocol);
            if(sockfd == -1) continue;
            SocketCloser closer(sockfd);
            if(connect(sockfd, p->ai_addr, p->ai_addrlen) == 0) {
                closer.release();
                break;
            }
        }
        if(sockfd == -1) throw std::system_error(errno, std::system_category(), "connect");
        SocketCloser closer(sockfd);

        size_t sent = 0;
        while(sent < msg.size()) {
            ssize_t res = ::send(sockfd, msg.data() + sent, msg.size() - sent, 0);
            if(res == -1) throw std::system_error(errno, std::system_category(), "send");
            sent += res;
        }

        std::string response;
        char buf[4096];
        while(true) {
            ssize_t received = recv(sockfd, buf, sizeof(buf), 0);
            if(received == -1) throw std::system_error(errno, std::system_category(), "recv");
            if(received == 0) break;
            response.append(buf, received);
        }
        return response;
    }

    std::vector<std::string> sendMulticast(const std::string& msg, const std::string& adr, int port, std::chrono::steady_clock::duration timeout) {
        addrinfo hints{}, *res;
        hints.ai_family = AF_UNSPEC;
        hints.ai_socktype = SOCK_DGRAM;

        int status = getaddrinfo(adr.c_str(), std::to_string(port).c_str(), &hints, &res);
        if(status != 0) throw std::system_error(errno, std::system_category(), "getaddrinfo");
        std::unique_ptr<addrinfo, decltype(&freeaddrinfo)> ai(res, freeaddrinfo);

        int sockfd = -1;
        for(auto p = res; p; p = p->ai_next) {
            sockfd = socket(p->ai_family, p->ai_socktype, p->ai_protocol);
            if(sockfd == -1) continue;

            int ttl = 1;
            if(p->ai_family == AF_INET) {
                if(setsockopt(sockfd, IPPROTO_IP, IP_MULTICAST_TTL, &ttl, sizeof(ttl)) { close(sockfd); continue; }
            } else if(p->ai_family == AF_INET6) {
                if(setsockopt(sockfd, IPPROTO_IPV6, IPV6_MULTICAST_HOPS, &ttl, sizeof(ttl))) { close(sockfd); continue; }
            }
            break;
        }
        if(sockfd == -1) throw std::system_error(errno, std::system_category(), "socket");
        SocketCloser closer(sockfd);

        ssize_t sent = sendto(sockfd, msg.data(), msg.size(), 0, res->ai_addr, res->ai_addrlen);
        if(sent == -1) throw std::system_error(errno, std::system_category(), "sendto");

        int flags = fcntl(sockfd, F_GETFL);
        if(flags == -1) throw std::system_error(errno, std::system_category(), "fcntl");
        if(fcntl(sockfd, F_SETFL, flags | O_NONBLOCK) == -1) throw std::system_error(errno, std::system_category(), "fcntl");

        std::vector<std::string> responses;
        auto deadline = std::chrono::steady_clock::now() + timeout;
        while(true) {
            timeval tv;
            auto now = std::chrono::steady_clock::now();
            if(now >= deadline) break;
            auto remaining = std::chrono::duration_cast<std::chrono::microseconds>(deadline - now);
            tv.tv_sec = remaining.count() / 1000000;
            tv.tv_usec = remaining.count() % 1000000;

            fd_set fds;
            FD_ZERO(&fds);
            FD_SET(sockfd, &fds);
            int ready = select(sockfd + 1, &fds, nullptr, nullptr, &tv);
            if(ready == -1) throw std::system_error(errno, std::system_category(), "select");
            if(ready == 0) break;

            char buf[65536];
            while(true) {
                ssize_t received = recvfrom(sockfd, buf, sizeof(buf), 0, nullptr, nullptr);
                if(received == -1) {
                    if(errno == EAGAIN || errno == EWOULDBLOCK) break;
                    throw std::system_error(errno, std::system_category(), "recvfrom");
                }
                responses.emplace_back(buf, received);
            }
        }
        return responses;
    }
};

