class ThreadCtx;
class WdtSocket;
struct EncryptionParams;
enum class ErrorCode { OK, CONNECTION_ERROR, CONNECTION_FAILED };

class ClientSocket {
private:
    std::string dest_;
    ThreadCtx& threadCtx_;
    int port_;
    struct addrinfo sa_;
    std::unique_ptr<WdtSocket> socket_;
    std::string peerIp_;

    void closeConnection() {
        if (socket_) {
            socket_->close();
        }
    }

public:
    ClientSocket(ThreadCtx& threadCtx, const std::string& dest, int port,
                 const EncryptionParams& encryptionParams, int64_t ivChangeInterval)
        : threadCtx_(threadCtx), dest_(dest), port_(port),
          socket_(std::make_unique<WdtSocket>(encryptionParams, ivChangeInterval, threadCtx)) {}

    ErrorCode connect() {
        struct addrinfo hints, *res = nullptr, *rp;
        memset(&hints, 0, sizeof(hints));
        hints.ai_family = AF_UNSPEC;
        hints.ai_socktype = SOCK_STREAM;
        hints.ai_protocol = IPPROTO_TCP;

        std::string portStr = std::to_string(port_);
        int ret = getaddrinfo(dest_.c_str(), portStr.c_str(), &hints, &res);
        if (ret != 0) {
            return ErrorCode::CONNECTION_ERROR;
        }

        ErrorCode error = ErrorCode::CONNECTION_FAILED;
        int sockFd = -1;

        for (rp = res; rp != nullptr; rp = rp->ai_next) {
            sockFd = socket(rp->ai_family, rp->ai_socktype, rp->ai_protocol);
            if (sockFd == -1) continue;

            int flags = fcntl(sockFd, F_GETFL, 0);
            if (flags == -1 || fcntl(sockFd, F_SETFL, flags | O_NONBLOCK) == -1) {
                close(sockFd);
                continue;
            }

            if (::connect(sockFd, rp->ai_addr, rp->ai_addrlen) == 0) {
                fcntl(sockFd, F_SETFL, flags & ~O_NONBLOCK);
                socket_->setFd(sockFd);
                char ipStr[INET6_ADDRSTRLEN];
                if (rp->ai_family == AF_INET) {
                    auto* sa = reinterpret_cast<sockaddr_in*>(rp->ai_addr);
                    inet_ntop(AF_INET, &sa->sin_addr, ipStr, sizeof(ipStr));
                } else {
                    auto* sa = reinterpret_cast<sockaddr_in6*>(rp->ai_addr);
                    inet_ntop(AF_INET6, &sa->sin6_addr, ipStr, sizeof(ipStr));
                }
                peerIp_ = ipStr;
                error = ErrorCode::OK;
                break;
            } else if (errno == EINPROGRESS) {
                fd_set set;
                FD_ZERO(&set);
                FD_SET(sockFd, &set);
                timeval tv;
                tv.tv_sec = threadCtx_.getConnectTimeout();
                tv.tv_usec = 0;

                if (select(sockFd + 1, nullptr, &set, nullptr, &tv) <= 0) {
                    close(sockFd);
                    continue;
                }

                int so_error;
                socklen_t len = sizeof(so_error);
                getsockopt(sockFd, SOL_SOCKET, SO_ERROR, &so_error, &len);
                if (so_error != 0) {
                    close(sockFd);
                    continue;
                }

                fcntl(sockFd, F_SETFL, flags & ~O_NONBLOCK);
                socket_->setFd(sockFd);
                char ipStr[INET6_ADDRSTRLEN];
                if (rp->ai_family == AF_INET) {
                    auto* sa = reinterpret_cast<sockaddr_in*>(rp->ai_addr);
                    inet_ntop(AF_INET, &sa->sin_addr, ipStr, sizeof(ipStr));
                } else {
                    auto* sa = reinterpret_cast<sockaddr_in6*>(rp->ai_addr);
                    inet_ntop(AF_INET6, &sa->sin6_addr, ipStr, sizeof(ipStr));
                }
                peerIp_ = ipStr;
                error = ErrorCode::OK;
                break;
            } else {
                close(sockFd);
            }
        }

        freeaddrinfo(res);

        if (error != ErrorCode::OK) return error;

        setSendBufferSize();
        return ErrorCode::OK;
    }

    const std::string& getPeerIp() const { return peerIp_; }

    void setSendBufferSize() {
        int bufSize = threadCtx_.getSendBufferSize();
        if (bufSize <= 0) return;
        int fd = socket_->getFd();
        setsockopt(fd, SOL_SOCKET, SO_SNDBUF, &bufSize, sizeof(bufSize));
    }
};

