    TCPClient(const std::string& addr, uint16_t p) : address(addr), port(p), main_socket(INVALID_SOCKET_VALUE), connected(false) {}
    ~TCPClient() { Disconnect(); }

    bool Connect() {
        if (connected) Disconnect();
#ifdef _WIN32
        WSADATA wsaData;
        if (WSAStartup(MAKEWORD(2, 2), &wsaData) != 0) return false;
