        size_t totalRead = 0;
        while (totalRead < size) {
            auto now = std::chrono::steady_clock::now();
            auto elapsed = now - start;
            if (elapsed >= timeout) return false;
            auto remaining = timeout - elapsed;
            fd_set readSet;
            FD_ZERO(&readSet);
            FD_SET(main_socket, &readSet);
            timeval tv;
            auto micros = std::chrono::duration_cast<std::chrono::microseconds>(remaining);
            tv.tv_sec = micros.count() / 1000000;
            tv.tv_usec = micros.count() % 1000000;
#ifdef _WIN32
            int selectResult = select(0, &readSet, nullptr, nullptr, &tv);
#else
            int selectResult = select(main_socket + 1, &readSet, nullptr, nullptr, &tv);
#endif
            if (selectResult <= 0) return false;
#ifdef _WIN32
            int bytesRead = recv(main_socket, static_cast<char*>(buffer) + totalRead, (int)(size - totalRead), 0);
            if (bytesRead == SOCKET_ERROR) {
                int error = WSAGetLastError();
                if (error == WSAEWOULDBLOCK) continue;
                return false;
            }
#else
            ssize_t bytesRead = recv(main_socket, static_cast<char*>(buffer) + totalRead, size - totalRead, 0);
            if (bytesRead == -1) {
                if (errno == EWOULDBLOCK || errno == EAGAIN) continue;
                return false;
            }
#endif
            if (bytesRead == 0) return false;
            totalRead += bytesRead;
        }
        return true;
    }

    bool Write(const void* buffer, size_t size, const std::chrono::nanoseconds& timeout) {
        if (!connected) return false;
        auto start = std::chrono::steady_clock::now();
        size_t totalWritten = 0;
        while (totalWritten < size) {
            auto now = std::chrono::steady_clock::now();
            auto elapsed = now - start;
            if (elapsed >= timeout) return false;
            auto remaining = timeout - elapsed;
            fd_set writeSet;
            FD_ZERO(&writeSet);
            FD_SET(main_socket, &writeSet);
            timeval tv;
            auto micros = std::chrono::duration_cast<std::chrono::microseconds>(remaining);
            tv.tv_sec = micros.count() / 1000000;
            tv.tv_usec = micros.count() % 1000000;
#ifdef _WIN32
            int selectResult = select(0, nullptr, &writeSet, nullptr, &tv);
#else
            int selectResult = select(main_socket + 1, nullptr, &writeSet, nullptr, &tv);
#endif
            if (selectResult <= 0) return false;
#ifdef _WIN32
            int bytesSent = send(main_socket, static_cast<const char*>(buffer) + totalWritten, (int)(size - totalWritten), 0);
            if (bytesSent == SOCKET_ERROR) {
                int error = WSAGetLastError();
                if (error == WSAEWOULDBLOCK) continue;
                return false;
            }
#else
            ssize_t bytesSent = send(main_socket, static_cast<const char*>(buffer) + totalWritten, size - totalWritten, 0);
            if (bytesSent == -1) {
                if (errno == EWOULDBLOCK || errno == EAGAIN) continue;
                return false;
            }
#endif
            if (bytesSent == 0) return false;
            totalWritten += bytesSent;
        }
        return true;
    }

    void Disconnect() {
        if (connected) {
#ifdef _WIN32
            closesocket(main_socket);
            WSACleanup();
#else
            close(main_socket);
#endif
            connected = false;
            main_socket = INVALID_SOCKET_VALUE;
        }
    }

    bool SetNonBlock(SocketHolder sockFD, bool nonBlocking) {
#ifdef _WIN32
        u_long mode = nonBlocking ? 1 : 0;
        return ioctlsocket(sockFD, FIONBIO, &mode) == 0;
#else
        int flags = fcntl(sockFD, F_GETFL, 0);
        if (flags == -1) return false;
        flags = nonBlocking ? (flags | O_NONBLOCK) : (flags & ~O_NONBLOCK);
        return fcntl(sockFD, F_SETFL, flags) == 0;
#endif
    }

    bool ConnectWithTimeout(SocketHolder sockFD, void* server_addr, size_t server_addr_len, int timeout_seconds) {
        if (!SetNonBlock(sockFD, true)) return false;
#ifdef _WIN32
        int connectResult = connect(sockFD, static_cast<sockaddr*>(server_addr), (int)server_addr_len);
        if (connectResult == SOCKET_ERROR) {
            int error = WSAGetLastError();
            if (error != WSAEWOULDBLOCK) return false;
        }
#else
        int connectResult = connect(sockFD, static_cast<sockaddr*>(server_addr), server_addr_len);
        if (connectResult == -1 && errno != EINPROGRESS) return false;
#endif
        fd_set writeSet;
        FD_ZERO(&writeSet);
        FD_SET(sockFD, &writeSet);
        timeval tv;
        tv.tv_sec = timeout_seconds;
        tv.tv_usec = 0;
#ifdef _WIN32
        int selectResult = select(0, nullptr, &writeSet, nullptr, &tv);
#else
        int selectResult = select(sockFD + 1, nullptr, &writeSet, nullptr, &tv);
#endif
        if (selectResult <= 0) return false;
        int errorCode = 0;
        socklen_t errorLen = sizeof(errorCode);
#ifdef _WIN32
        if (getsockopt(sockFD, SOL_SOCKET, SO_ERROR, (char*)&errorCode, &errorLen) != 0) return false;
#else
        if (getsockopt(sockFD, SOL_SOCKET, SO_ERROR, &errorCode, &errorLen) != 0) return false;
#endif
        if (errorCode != 0) return false;
        return SetNonBlock(sockFD, false);
    }
};

