        ~MemoryStruct() { free(memory); }
    };

    static size_t WriteMemoryCallback(void *contents, size_t size, size_t nmemb, void *userp) {
        size_t realsize = size * nmemb;
        MemoryStruct *mem = static_cast<MemoryStruct*>(userp);
        char *ptr = static_cast<char*>(realloc(mem->memory, mem->size + realsize + 1));
        if (!ptr) return 0;
        mem->memory = ptr;
        memcpy(&mem->memory[mem->size], contents, realsize);
        mem->size += realsize;
        mem->memory[mem->size] = '\0';
        return realsize;
    }

public:
    long downloadPage(const std::string& url, std::string& res, const std::vector<std::string>& headers = {}, const std::string& body = "") {
        CURL *curl = curl_easy_init();
        if (!curl) return 0;
        MemoryStruct chunk;
        struct curl_slist *header_list = nullptr;
        for (const auto& h : headers)
            header_list = curl_slist_append(header_list, h.c_str());
        curl_easy_setopt(curl, CURLOPT_URL, url.c_str());
        if (header_list)
            curl_easy_setopt(curl, CURLOPT_HTTPHEADER, header_list);
        if (!body.empty()) {
            curl_easy_setopt(curl, CURLOPT_CUSTOMREQUEST, "GET");
            curl_easy_setopt(curl, CURLOPT_POSTFIELDS, body.c_str());
            curl_easy_setopt(curl, CURLOPT_POSTFIELDSIZE, body.size());
        }
        curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, WriteMemoryCallback);
        curl_easy_setopt(curl, CURLOPT_WRITEDATA, &chunk);
        CURLcode curl_res = curl_easy_perform(curl);
        long http_code = 0;
        if (curl_res == CURLE_OK)
            curl_easy_getinfo(curl, CURLINFO_RESPONSE_CODE, &http_code);
        res.assign(chunk.memory ? chunk.memory : "", chunk.size);
        curl_slist_free_all(header_list);
        curl_easy_cleanup(curl);
        return http_code;
    }

    long getRequest(const std::string& url, json& res, const std::vector<std::string>& headers = {}, const std::string& body = "") {
        std::string response;
        long http_code = downloadPage(url, response, headers, body);
        if (!response.empty()) res = json::parse(response);
        return http_code;
    }
};

