#include <memory>
#include <stdexcept>

class Stats {
private:
    static Stats* instance_;
    static std::mutex instance_mutex_;
    static bool initialized_;

    std::string stats_socket_path_;
    int sockfd_;
    sockaddr_un serv_addr_;
    std::thread stats_thread_;
    std::atomic<bool> statsThreadRunning_;
    std::mutex stats_mutex_;
    std::mutex thread_mutex_;
    std::condition_variable thread_exited_;
    int thread_count_;
    std::unordered_map<std::string, int> stats_;

    Stats(const std::string& stats_socket_path) : stats_socket_path_(stats_socket_path), sockfd_(-1), statsThreadRunning_(false), thread_count_(0) {
        startSocket();
    }

    bool startSocket() {
        sockfd_ = socket(AF_UNIX, SOCK_STREAM, 0);
        if (sockfd_ < 0) return false;

        memset(&serv_addr_, 0, sizeof(serv_addr_));
        serv_addr_.sun_family = AF_UNIX;
        strncpy(serv_addr_.sun_path, stats_socket_path_.c_str(), sizeof(serv_addr_.sun_path)-1);

        unlink(stats_socket_path_.c_str());
        if (bind(sockfd_, (struct sockaddr*)&serv_addr_, sizeof(serv_addr_)) < 0) {
            close(sockfd_);
            return false;
        }

        if (listen(sockfd_, 5) < 0) {
            close(sockfd_);
            return false;
        }

        statsThreadRunning_ = true;
        stats_thread_ = std::thread(&Stats::runSocket, this);
        return true;
    }

    void runSocket() {
        while (statsThreadRunning_) {
            int client_sock = accept(sockfd_, nullptr, nullptr);
            if (client_sock < 0) {
                if (statsThreadRunning_) continue;
                else break;
            }

            std::thread([this, client_sock]() {
                {
                    std::lock_guard<std::mutex> lock(thread_mutex_);
                    thread_count_++;
                }
                processMsg(client_sock);
                {
                    std::lock_guard<std::mutex> lock(thread_mutex_);
                    thread_count_--;
                }
                thread_exited_.notify_one();
            }).detach();
        }
    }

    void processMsg(int sockfd) {
        char buffer[1024];
        ssize_t n = read(sockfd, buffer, sizeof(buffer)-1);
        if (n <= 0) {
            close(sockfd);
            return;
        }
        buffer[n] = '\0';
        std::string cmd(buffer);
        std::istringstream iss(cmd);
        std::string action, key;
        int val;
        std::string response;

        iss >> action;
        if (action == "GET") {
            iss >> key;
            if (key == "all") {
                std::lock_guard<std::mutex> lock(stats_mutex_);
                for (const auto& pair : stats_) {
                    response += pair.first + " " + std::to_string(pair.second) + "\n";
                }
                if (response.empty()) response = "No stats\n";
            } else {
                std::lock_guard<std::mutex> lock(stats_mutex_);
                auto it = stats_.find(key);
                response = (it != stats_.end()) ? std::to_string(it->second) : "0";
            }
        } else if (action == "INCREMENT") {
            iss >> key >> val;
            std::lock_guard<std::mutex> lock(stats_mutex_);
            stats_[key] += val;
            response = std::to_string(stats_[key]);
        } else if (action == "SET") {
            iss >> key >> val;
            std::lock_guard<std::mutex> lock(stats_mutex_);
            stats_[key] = val;
            response = std::to_string(val);
        } else if (action == "RESET") {
            std::lock_guard<std::mutex> lock(stats_mutex_);
            stats_.clear();
            response = "OK";
        } else {
            response = "ERROR";
        }

        write(sockfd, response.c_str(), response.size());
        close(sockfd);
    }

public:
    ~Stats() {
        statsThreadRunning_ = false;
        if (sockfd_ != -1) {
            close(sockfd_);
            sockfd_ = -1;
        }
        if (stats_thread_.joinable()) {
            stats_thread_.join();
        }
        std::unique_lock<std::mutex> lock(thread_mutex_);
        thread_exited_.wait(lock, [this](){ return thread_count_ == 0; });
        unlink(stats_socket_path_.c_str());
    }

    static Stats& get(const std::string& stats_socket_path) {
        std::lock_guard<std::mutex> lock(instance_mutex_);
        if (!instance_) {
            instance_ = new Stats(stats_socket_path);
            initialized_ = true;
        }
        return *instance_;
    }

    static std::unique_ptr<Stats> get_for_unittest(const std::string& stats_socket_path) {
        return std::unique_ptr<Stats>(new Stats(stats_socket_path));
    }

    static bool init(const std::string& stats_socket_path) {
        std::lock_guard<std::mutex> lock(instance_mutex_);
        if (initialized_) return false;
        instance_ = new Stats(stats_socket_path);
        initialized_ = true;
        return true;
    }

    static bool isInit() {
        std::lock_guard<std::mutex> lock(instance_mutex_);
        return initialized_;
    }

    std::unordered_map<std::string, int> getAll() {
        std::lock_guard<std::mutex> lock(stats_mutex_);
        return stats_;
    }

    int increment(const std::string& key, int val) {
        std::lock_guard<std::mutex> lock(stats_mutex_);
        return stats_[key] += val;
    }

    int set(const std::string& key, int val) {
        std::lock_guard<std::mutex> lock(stats_mutex_);
        stats_[key] = val;
        return val;
    }

    int reset() {
        std::lock_guard<std::mutex> lock(stats_mutex_);
        stats_.clear();
        return 0;
    }
};

Stats* Stats::instance_ = nullptr;
std::mutex Stats::instance_mutex_;
bool Stats::initialized_ = false;

