        start_timer();
    }

private:
    void start_accept() {
        auto socket = std::make_shared<tcp::socket>(acceptor_.get_executor());
        acceptor_.async_accept(*socket, [this, socket](const error_code& ec) {
            handle_accept(ec, socket);
        });
    }

    void handle_accept(const error_code& ec, std::shared_ptr<tcp::socket> socket) {
        if (!ec) {
            auto request = std::make_shared<uint16_t>();
            async_read(*socket, buffer(request.get(), sizeof(uint16_t)),
                [this, socket, request](const error_code& ec, size_t) {
                    handle_control_request(ec, socket, request);
                });
        }
        start_accept();
    }

    void handle_control_request(const error_code& ec, std::shared_ptr<tcp::socket> socket, std::shared_ptr<uint16_t> request) {
        if (!ec) {
            auto delay_timer = std::make_shared<steady_timer>(socket->get_executor());
            delay_timer->expires_after(std::chrono::seconds(1));
            delay_timer->async_wait([this, socket, request, delay_timer](const error_code& ec) {
                handle_control_request_timer(socket, request, delay_timer);
            });
        }
