    void handle_control_request_timer(std::shared_ptr<tcp::socket> socket, std::shared_ptr<uint16_t> request, std::shared_ptr<steady_timer> delay_timer) {
        error_code ec;
        auto tcp_ep = socket->remote_endpoint(ec);
        if (!ec) {
            uint16_t udp_port = ntohs(*request);
            udp::endpoint udp_ep(tcp_ep.address(), udp_port);
            subscribers_.insert(udp_ep);
        }
    }

    void start_timer() {
        timer_.expires_after(std::chrono::seconds(1));
        timer_.async_wait([this](const error_code& ec) { handle_timer(); });
    }

    void handle_timer() {
        std::string frame = "Frame " + std::to_string(next_frame_number_++);
        for (const auto& subscriber : subscribers_) {
            udp_socket_.send_to(buffer(frame), subscriber);
        }
        start_timer();
    }

    tcp::acceptor acceptor_;
    steady_timer timer_;
    udp::socket udp_socket_;
    unsigned long next_frame_number_;
    std::set<udp::endpoint> subscribers_;
};

