#include <curl/curl.h>
#include <string>
#include <fstream>
#include <utime.h>

class DownloadTask {
public:
    static void initialize() { curl_global_init(CURL_GLOBAL_DEFAULT); }
    static void uninitialize() { curl_global_cleanup(); }

    DownloadTask(const std::string& url, const std::string& output, const std::string& defaultFile, time_t mtime, const std::string& name = "")
        : m_url(url), m_output(output), m_default(defaultFile), m_mtime(mtime), m_name(name) {
        m_outputTmp = m_output + ".tmp";
        m_retries = 0;
        m_userAgent = "libcurl-agent/1.0";
    }

    bool run() {
        bool success = false;
        const int maxRetries = 3;

        for (int i = 0; i < maxRetries; ++i) {
            if (downloadFile(m_url)) {
                success = true;
                break;
            }
            m_retries++;
        }

        if (!success && !m_urlBackup.empty()) {
            for (int i = 0; i < maxRetries; ++i) {
                if (downloadFile(m_urlBackup)) {
                    success = true;
                    break;
                }
                m_retries++;
            }
        }

        if (success) {
            if (std::rename(m_outputTmp.c_str(), m_output.c_str()) != 0) {
                m_error = "Failed to rename temporary file";
                success = false;
            } else {
                utimbuf times = {m_mtime, m_mtime};
                utime(m_output.c_str(), &times);
            }
        }

        if (!success && !m_default.empty()) {
            std::ifstream src(m_default, std::ios::binary);
            std::ofstream dst(m_output, std::ios::binary);
            if (src && dst) dst << src.rdbuf();
        }

        std::remove(m_outputTmp.c_str());
        return success;
    }

    unsigned int getRetries() const { return m_retries; }

private:
    std::string m_url;
    std::string m_urlBackup;
    std::string m_output;
    std::string m_outputTmp;
    std::string m_default;
    time_t m_mtime;
    unsigned int m_retries;
    std::string m_name;
    std::string m_userAgent;
    std::string m_error;

    static size_t writeData(void* buffer, size_t size, size_t nmemb, FILE* fp) {
        return fwrite(buffer, size, nmemb, fp);
    }

    bool downloadFile(const std::string& url) {
        CURL* curl = curl_easy_init();
        if (!curl) {
            m_error = "CURL initialization failed";
            return false;
        }

        FILE* fp = fopen(m_outputTmp.c_str(), "wb");
        if (!fp) {
            m_error = "Failed to open temporary file";
            curl_easy_cleanup(curl);
            return false;
        }

        curl_easy_setopt(curl, CURLOPT_URL, url.c_str());
        curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, &DownloadTask::writeData);
        curl_easy_setopt(curl, CURLOPT_WRITEDATA, fp);
        curl_easy_setopt(curl, CURLOPT_USERAGENT, m_userAgent.c_str());
        curl_easy_setopt(curl, CURLOPT_FOLLOWLOCATION, 1L);
        curl_easy_setopt(curl, CURLOPT_FAILONERROR, 1L);

        CURLcode res = curl_easy_perform(curl);
        fclose(fp);

        if (res != CURLE_OK) {
            m_error = curl_easy_strerror(res);
            curl_easy_cleanup(curl);
            return false;
        }

        long httpCode = 0;
        curl_easy_getinfo(curl, CURLINFO_RESPONSE_CODE, &httpCode);
        curl_easy_cleanup(curl);
        return httpCode >= 200 && httpCode < 300;
    }
};

