public:
    HttpWebResponse(int sc, const std::map<std::string, std::string>& h, std::iostream* s) : status_code_(sc), headers_(h), stream_(s) {}
    int GetStatusCode() const { return status_code_; }
    const std::map<std::string, std::string>& GetHeaders() const { return headers_; }
    std::iostream* GetStream() const { return stream_; }
};

class HttpWebRequest {
    std::string uri_;
    std::string method_;
    std::map<std::string, std::string> header_;
    std::stringstream is_;
public:
    HttpWebRequest(std::string &uri) : uri_(uri), method_("GET") {}

    static size_t callback(void *buffer, size_t size, size_t nmemb, void *data) {
        std::stringstream* stream = static_cast<std::stringstream*>(data);
        stream->write(static_cast<char*>(buffer), size * nmemb);
        return size * nmemb;
    }

    static size_t header_callback(char *buffer, size_t size, size_t nitems, void *userdata) {
        std::map<std::string, std::string>* headers = static_cast<std::map<std::string, std::string>*>(userdata);
        std::string line(buffer, buffer + size * nitems);
        size_t colon = line.find(':');
        if (colon != std::string::npos) {
            std::string key = line.substr(0, colon);
            std::string value = line.substr(colon + 1);
            value.erase(0, value.find_first_not_of(" \t"));
            value.erase(value.find_last_not_of(" \t") + 1);
            (*headers)[key] = value;
        }
        return size * nitems;
    }

    HttpWebResponse* GetFileResponse(const char *uri) {
        std::string path = uri;
        if (path.find("file://") == 0) path = path.substr(7);
        std::ifstream file(path, std::ios::binary);
        if (file) {
            is_.str("");
            is_.clear();
            is_ << file.rdbuf();
            file.close();
            return new HttpWebResponse(200, {}, &is_);
        }
        return new HttpWebResponse(404, {}, nullptr);
    }

    HttpWebResponse* GetNetResponse(const char *uri) {
        CURL *curl = curl_easy_init();
        if (!curl) return new HttpWebResponse(500, {}, nullptr);
        std::map<std::string, std::string> resp_headers;
        struct curl_slist *headers = nullptr;
        for (auto& h : header_) headers = curl_slist_append(headers, (h.first + ": " + h.second).c_str());
        is_.str("");
        is_.clear();
        curl_easy_setopt(curl, CURLOPT_URL, uri);
        curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, callback);
        curl_easy_setopt(curl, CURLOPT_WRITEDATA, &is_);
        curl_easy_setopt(curl, CURLOPT_HEADERFUNCTION, header_callback);
        curl_easy_setopt(curl, CURLOPT_HEADERDATA, &resp_headers);
        curl_easy_setopt(curl, CURLOPT_HTTPHEADER, headers);
        if (method_ != "GET") curl_easy_setopt(curl, CURLOPT_CUSTOMREQUEST, method_.c_str());
        CURLcode res = curl_easy_perform(curl);
        long http_code = 0;
        if (res != CURLE_OK) {
            curl_slist_free_all(headers);
            curl_easy_cleanup(curl);
            return new HttpWebResponse(500, {}, nullptr);
        }
        curl_easy_getinfo(curl, CURLINFO_RESPONSE_CODE, &http_code);
        curl_slist_free_all(headers);
        curl_easy_cleanup(curl);
        return new HttpWebResponse(http_code, resp_headers, &is_);
    }

    HttpWebResponse* GetResponse() {
        if (uri_.find("file://") == 0) return GetFileResponse(uri_.c_str());
        else return GetNetResponse(uri_.c_str());
    }
};

