class Task {
public:
    Task(const std::string& url, const std::string& output) : m_url(url), m_output(output) {}

    void run() {
        CURL* curl = curl_easy_init();
        if (curl) {
            std::ofstream file(m_output, std::ios::binary);
            if (!file.is_open()) {
                std::cerr << "Failed to open file: " << m_output << std::endl;
                curl_easy_cleanup(curl);
                return;
            }
            curl_easy_setopt(curl, CURLOPT_URL, m_url.c_str());
            curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, &Task::writeData);
            curl_easy_setopt(curl, CURLOPT_WRITEDATA, &file);
            CURLcode res = curl_easy_perform(curl);
            if (res != CURLE_OK) {
                std::cerr << "curl_easy_perform() failed: " << curl_easy_strerror(res) << std::endl;
            }
            curl_easy_cleanup(curl);
        }
    }

private:
    static size_t writeData(void* buffer, size_t size, size_t nmemb, void* userp) {
        std::ofstream* file = static_cast<std::ofstream*>(userp);
        if (file && file->is_open()) {
            file->write(static_cast<const char*>(buffer), size * nmemb);
            return size * nmemb;
        }
        return 0;
    }

    std::string m_url;
    std::string m_output;
};

class DownloadPool {
public:
    DownloadPool() {
        curl_global_init(CURL_GLOBAL_ALL);
        unsigned num_threads = std::thread::hardware_concurrency();
        if (num_threads == 0) num_threads = 4;
        m_threads.reserve(num_threads);
        for (unsigned i = 0; i < num_threads; ++i) {
            m_threads.emplace_back(&DownloadPool::run, this);
        }
    }

    ~DownloadPool() {
        finishAndWaitForExit();
        curl_global_cleanup();
    }

    void addTask(const std::string& url, const std::string& output) {
        std::lock_guard<std::mutex> lock(m_mtx);
        if (m_urls.find(url) == m_urls.end()) {
            m_urls.insert(url);
            m_queue.emplace(url, output);
        }
    }

    void setNoMoreTask() {
        std::lock_guard<std::mutex> lock(m_mtx);
        m_noMoreTask = true;
    }

    void finishAndWaitForExit() {
        setNoMoreTask();
        for (auto& thread : m_threads) {
            if (thread.joinable()) {
                thread.join();
            }
        }
    }

private:
    void run() {
        while (true) {
            std::unique_lock<std::mutex> lock(m_mtx);
            if (!m_queue.empty()) {
                Task task = std::move(m_queue.front());
                m_queue.pop();
                lock.unlock();
                task.run();
            } else {
                if (m_noMoreTask) {
                    break;
                }
                lock.unlock();
                std::this_thread::sleep_for(std::chrono::milliseconds(100));
            }
        }
    }

    std::vector<std::thread> m_threads;
    std::queue<Task> m_queue;
    std::unordered_set<std::string> m_urls;
    std::mutex m_mtx;
    bool m_noMoreTask = false;
};

