const int MAXSOCK = 10;

class NetworkClientInfo {
public:
    int socket;
    // Additional client info can be added here
};

class NetworkServer {
private:
    std::string host;
    unsigned short port_num;
    bool server_online;
    bool server_listening;
    std::vector<NetworkClientInfo*> ServerClients;
    std::mutex ServerClientsMutex;
    int server_sock[MAXSOCK];
    int socket_count;
    std::thread* ConnectionThread[MAXSOCK];

public:
    NetworkServer(std::string host, unsigned short port) : host(host), port_num(port), server_online(false), server_listening(false), socket_count(0) {
        for (int i = 0; i < MAXSOCK; ++i) {
            server_sock[i] = -1;
            ConnectionThread[i] = nullptr;
        }
    }

    ~NetworkServer() {
        StopServer();
    }

    void StartServer() {
        if (server_online) return;

        sockaddr_in6 server_addr;
        memset(&server_addr, 0, sizeof(server_addr));
        server_addr.sin6_family = AF_INET6;
        server_addr.sin6_addr = in6addr_any;
        server_addr.sin6_port = htons(port_num);

        for (int i = 0; i < MAXSOCK; ++i) {
            server_sock[i] = socket(AF_INET6, SOCK_STREAM, 0);
            if (server_sock[i] == -1) {
                std::cerr << "Failed to create socket." << std::endl;
                continue;
            }

            int opt = 1;
            if (setsockopt(server_sock[i], SOL_SOCKET, SO_REUSEADDR, &opt, sizeof(opt)) < 0) {
                std::cerr << "Failed to set socket options." << std::endl;
                close(server_sock[i]);
                continue;
            }

            if (bind(server_sock[i], (struct sockaddr*)&server_addr, sizeof(server_addr)) < 0) {
                std::cerr << "Failed to bind socket." << std::endl;
                close(server_sock[i]);
                continue;
            }

            if (listen(server_sock[i], 10) < 0) {
                std::cerr << "Failed to listen on socket." << std::endl;
                close(server_sock[i]);
                continue;
            }

            ConnectionThread[i] = new std::thread(&NetworkServer::ConnectionThreadFunction, this, i);
            socket_count++;
        }

        server_online = true;
        server_listening = true;
    }

    void StopServer() {
        server_online = false;
        server_listening = false;

        for (int i = 0; i < socket_count; ++i) {
            if (server_sock[i] != -1) {
                close(server_sock[i]);
                server_sock[i] = -1;
            }
            if (ConnectionThread[i] != nullptr) {
                ConnectionThread[i]->join();
                delete ConnectionThread[i];
                ConnectionThread[i] = nullptr;
            }
        }

        std::lock_guard<std::mutex> lock(ServerClientsMutex);
        for (auto client : ServerClients) {
            close(client->socket);
            delete client;
        }
        ServerClients.clear();
    }

    void ConnectionThreadFunction(int socket_idx) {
        while (server_online) {
            int client_sock = accept_select(server_sock[socket_idx]);
            if (client_sock < 0) {
                continue;
            }

            std::lock_guard<std::mutex> lock(ServerClientsMutex);
            NetworkClientInfo* new_client = new NetworkClientInfo();
            new_client->socket = client_sock;
            ServerClients.push_back(new_client);
            // ClientInfoChanged(); // Notify if needed
        }
    }

    int accept_select(int sockfd) {
        fd_set set;
        struct timeval timeout;
        FD_ZERO(&set);
        FD_SET(sockfd, &set);
        timeout.tv_sec = 5;
        timeout.tv_usec = 0;

        int rv = select(sockfd + 1, &set, NULL, NULL, &timeout);
        if (rv == -1) {
            std::cerr << "Select error." << std::endl;
            return -1;
        } else if (rv == 0) {
            // Timeout, no incoming connection
            return -1;
        }

        sockaddr_in6 client_addr;
        socklen_t client_addr_len = sizeof(client_addr);
        int client_sock = accept(sockfd, (struct sockaddr*)&client_addr, &client_addr_len);
        if (client_sock < 0) {
            std::cerr << "Accept error." << std::endl;
            return -1;
        }

        return client_sock;
    }
};
