#include <iostream>
#include <string>
#include <memory>
#include <thread>
#include <chrono>
#include <cstring>
#include <sys/types.h>
#include <sys/socket.h>
#include <netdb.h>
#include <unistd.h>

class ThreadCtx {
public:
    int sendBufferSize;
};

class EncryptionParams {
    // Encryption parameters implementation
};

class WdtSocket {
public:
    WdtSocket(int sock) : sock_(sock) {}
    ~WdtSocket() { close(sock_); }
    int get() const { return sock_; }
private:
    int sock_;
};

class ClientSocket {
public:
    ClientSocket(ThreadCtx& threadCtx, const std::string& dest, int port, const EncryptionParams& encryptionParams, int64_t ivChangeInterval)
        : threadCtx_(threadCtx), dest_(dest), peerIp_("") {
        memset(&sa_, 0, sizeof(sa_));
        sa_.ai_family = AF_UNSPEC;
        sa_.ai_socktype = SOCK_STREAM;
        std::string portStr = std::to_string(port);
        getaddrinfo(dest_.c_str(), portStr.c_str(), &hints_, &result_);
        socket_ = std::make_unique<WdtSocket>(socket(result_->ai_family, result_->ai_socktype, result_->ai_protocol));
    }

    int connect() {
        if (socket_->get() < 0) return -1;
        if (::connect(socket_->get(), result_->ai_addr, result_->ai_addrlen) < 0) return -1;
        peerIp_ = inet_ntoa(((struct sockaddr_in*)result_->ai_addr)->sin_addr);
        return 0;
    }

    const std::string& getPeerIp() const {
        return peerIp_;
    }

    void setSendBufferSize() {
        setsockopt(socket_->get(), SOL_SOCKET, SO_SNDBUF, &threadCtx_.sendBufferSize, sizeof(threadCtx_.sendBufferSize));
    }

    void closeConnection() {
        socket_.reset();
    }

private:
    ThreadCtx& threadCtx_;
    std::string dest_;
