#ifdef _WIN32
#define WSAEAGAIN WSAEWOULDBLOCK
#define WSAEMULTIHOP -1
#define ERRNO(x) WSA##x
#define GET_ERRNO WSAGetLastError()
#define poll(x, y, z) WSAPoll(x, y, z);
#define SHUT_RD SD_RECEIVE
#define SHUT_WR SD_SEND
#define SHUT_RDWR SD_BOTH
#else
#define ERRNO(x) x
#define GET_ERRNO errno
#define closesocket(x) close(x)
#endif

class TCPClient {
public:
    using SocketHolder = int;

    TCPClient(const std::string& address, u16 port) : address(address), port(port) {}

    bool Connect() {
        if (connected)
            return true;

        struct addrinfo hints, *addrinfo;
        memset(&hints, 0, sizeof(hints));
        hints.ai_socktype = SOCK_STREAM;
        hints.ai_family = AF_INET;

        if (getaddrinfo(address.data(), NULL, &hints, &addrinfo) != 0) {
            return false;
        }

        main_socket = ::socket(AF_INET, SOCK_STREAM, 0);
        if (main_socket == static_cast<SocketHolder>(-1)) {
            return false;
        }

        if (!SetNonBlock(main_socket, true)) {
            closesocket(main_socket);
            return false;
        }

        struct sockaddr_in servaddr = {0};
        servaddr.sin_family = AF_INET;
        servaddr.sin_addr.s_addr = ((struct sockaddr_in*)(addrinfo->ai_addr))->sin_addr.s_addr;
        servaddr.sin_port = htons(port);
        freeaddrinfo(addrinfo);

        if (!ConnectWithTimeout(main_socket, &servaddr, sizeof(servaddr), 10)) {
            closesocket(main_socket);
            return false;
        }

        connected = true;
        return true;
    }

    bool Read(void* buffer, size_t size, const std::chrono::nanoseconds& timeout) {
        size_t read_bytes = 0;
        auto before = std::chrono::steady_clock::now();
        while (read_bytes != size) {
            int new_read = ::recv(main_socket, (char*)((uintptr_t)buffer + read_bytes), 
                (int)(size - read_bytes), 0);
            if (new_read < 0) {
                if (GET_ERRNO == ERRNO(EWOULDBLOCK) &&
                    (timeout == std::chrono::nanoseconds(0) ||
                     std::chrono::steady_clock::now() - before < timeout)) {
                    continue;
                }
                read_bytes = 0;
                break;
            }
            read_bytes += new_read;
        }
        return read_bytes == size;
    }

    bool Write(const void* buffer, size_t size, const std::chrono::nanoseconds& timeout) {
        size_t write_bytes = 0;
        auto before = std::chrono::steady_clock::now();
        while (write_bytes != size) {
            int new_written = ::send(main_socket, (const char*)((uintptr_t)buffer + write_bytes),
                                    (int)(size - write_bytes), 0);
            if (new_written < 0) {
                if (GET_ERRNO == ERRNO(EWOULDBLOCK) &&
                    (timeout == std::chrono::nanoseconds(0) ||
                     std::chrono::steady_clock::now() - before < timeout)) {
                    continue;
                }
                write_bytes = 0;
                break;
            }
            write_bytes += new_written;
        }
        return write_bytes == size;
    }

    void Disconnect() {
        if (connected) {
            shutdown(main_socket, SHUT_RDWR);
            closesocket(main_socket);
            connected = false;
        }
    }

private:
    bool SetNonBlock(SocketHolder sockFD, bool nonBlocking) {
        bool blocking = !nonBlocking;
#ifdef _WIN32
        unsigned long nonblocking = (blocking) ? 0 : 1;
        int ret = ioctlsocket(sockFD, FIONBIO, &nonblocking);
        if (ret == -1) {
            return false;
        }
#else
        int flags = ::fcntl(sockFD, F_GETFL, 0);
        if (flags == -1) {
            return false;
        }

        flags &= ~O_NONBLOCK;
        if (!blocking) {
            flags |= O_NONBLOCK;
        }

        const int ret = ::fcntl(sockFD, F_SETFL, flags);
        if (ret == -1) {
            return false;
        }
#endif
        return true;
    }

    bool ConnectWithTimeout(SocketHolder sockFD, void* server_addr, size_t server_addr_len,
                           int timeout_seconds) {
        int res = ::connect(sockFD, (struct sockaddr*)server_addr, static_cast<int>(server_addr_len));
        if (res == -1 && ((GET_ERRNO == ERRNO(EINPROGRESS) || GET_ERRNO == ERRNO(EWOULDBLOCK))) {
            struct timeval tv;
            fd_set fdset;
            FD_ZERO(&fdset);
            FD_SET(sockFD, &fdset);

            tv.tv_sec = timeout_seconds;
            tv.tv_usec = 0;
            int select_res = ::select(static_cast<int>(sockFD + 1), NULL, &fdset, NULL, &tv);
#ifdef _WIN32
            if (select_res == 0) {
                return false;
            }
#else
            bool select_good = false;
            if (select_res == 1) {
                int so_error;
                socklen_t len = sizeof so_error;

                getsockopt(sockFD, SOL_SOCKET, SO_ERROR, &so_error, &len);

                if (so_error == 0) {
                    select_good = true;
                }
            }
            if (!select_good) {
                return false;
            }
#endif
        } else if (res == -1) {
            return false;
        }
        return true;
    }

    std::string address;
    u16 port;
    SocketHolder main_socket = -1;
    bool connected = false;
};

