#include <unistd.h>
#include <fcntl.h>
#include <errno.h>

class TCPClient {
public:
    using SocketHolder = int;
    TCPClient(const std::string& address, uint16_t port) 
        : address(address), port(port), main_socket(-1), connected(false) {}

    bool Connect() {
        main_socket = socket(AF_INET, SOCK_STREAM, 0);
        if (main_socket < 0) return false;

        struct sockaddr_in server_addr;
        std::memset(&server_addr, 0, sizeof(server_addr));
        server_addr.sin_family = AF_INET;
        server_addr.sin_port = htons(port);
        inet_pton(AF_INET, address.c_str(), &server_addr.sin_addr);

        if (ConnectWithTimeout(main_socket, (struct sockaddr*)&server_addr, sizeof(server_addr), 5)) {
            connected = true;
            return true;
        }
        return false;
    }

    bool Read(void* buffer, size_t size, const std::chrono::nanoseconds& timeout) {
        if (!connected) return false;
        SetNonBlock(main_socket, true);
        fd_set read_fds;
        FD_ZERO(&read_fds);
        FD_SET(main_socket, &read_fds);
        struct timeval tv;
        tv.tv_sec = std::chrono::duration_cast<std::chrono::seconds>(timeout).count();
        tv.tv_usec = std::chrono::duration_cast<std::chrono::microseconds>(timeout).count() % 1000000;

        int result = select(main_socket + 1, &read_fds, nullptr, nullptr, &tv);
        if (result > 0 && FD_ISSET(main_socket, &read_fds)) {
            return recv(main_socket, buffer, size, 0) == static_cast<ssize_t>(size);
        }
        return false;
    }

    bool Write(const void* buffer, size_t size, const std::chrono::nanoseconds& timeout) {
        if (!connected) return false;
        SetNonBlock(main_socket, true);
        fd_set write_fds;
        FD_ZERO(&write_fds);
        FD_SET(main_socket, &write_fds);
        struct timeval tv;
        tv.tv_sec = std::chrono::duration_cast<std::chrono::seconds>(timeout).count();
        tv.tv_usec = std::chrono::duration_cast<std::chrono::microseconds>(timeout).count() % 1000000;

        int result = select(main_socket + 1, nullptr, &write_fds, nullptr, &tv);
        if (result > 0 && FD_ISSET(main_socket, &write_fds)) {
            return send(main_socket, buffer, size, 0) == static_cast<ssize_t>(size);
        }
        return false;
    }

    void Disconnect() {
        if (connected) {
            close(main_socket);
            connected = false;
        }
    }

private:
    std::string address;
    uint16_t port;
    SocketHolder main_socket;
    bool connected;

    bool SetNonBlock(SocketHolder sockFD, bool nonBlocking) {
        int flags = fcntl(sockFD, F_GETFL, 0);
        if (flags == -1) return false;
        if (nonBlocking) {
            flags |= O_NONBLOCK;
        } else {
            flags &= ~O_NONBLOCK;
        }
        return fcntl(sockFD, F_SETFL, flags) != -1;
    }

    bool ConnectWithTimeout(SocketHolder sockFD, void* server_addr, size_t server_addr_len, int timeout_seconds) {
        SetNonBlock(sockFD, true);
        if (connect(sockFD, server_addr, server_addr_len) < 0) {
            if (errno != EINPROGRESS) return false;
        }
        fd_set fdset;
        FD_ZERO(&fdset);
        FD_SET(sockFD, &fdset);
        struct timeval tv;
        tv.tv_sec = timeout_seconds;
        tv.tv_usec = 0;
        int result = select(sockFD + 1, nullptr, &fdset, nullptr, &tv);
        if (result > 0) {
            int so_error;
            socklen_t len = sizeof(so_error);
            if (getsockopt(sockFD, SOL_SOCKET, SO_ERROR, &so_error, &len) < 0) return false;
            return so_error == 0;
        }
        return false;
    }
};

