        memory.memory = (char*)malloc(1);
        memory.memory[0] = '\0';
    }

    ~CurlHttpClient() {
        free(memory.memory);
    }

    static size_t WriteMemoryCallback(void* contents, size_t size, size_t nmemb, void* userp) {
        size_t realsize = size * nmemb;
        MemoryStruct* mem = (MemoryStruct*)userp;

        char* ptr = (char*)realloc(mem->memory, mem->size + realsize + 1);
        if (ptr == NULL) return 0;

        mem->memory = ptr;
        memcpy(&(mem->memory[mem->size]), contents, realsize);
        mem->size += realsize;
        mem->memory[mem->size] = '\0';

        return realsize;
    }

    long downloadPage(const std::string& url, std::string& res, const std::vector<std::string>& headers, const std::string& body) {
        CURL* curl;
        CURLcode curl_res;
        long http_code = 0;

        curl = curl_easy_init();
        if(curl) {
            struct curl_slist* chunk = NULL;
            for (const auto& header : headers) {
                chunk = curl_slist_append(chunk, header.c_str());
            }

            curl_easy_setopt(curl, CURLOPT_URL, url.c_str());
            curl_easy_setopt(curl, CURLOPT_HTTPHEADER, chunk);
            curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, WriteMemoryCallback);
            curl_easy_setopt(curl, CURLOPT_WRITEDATA, (void*)&memory);
            curl_easy_setopt(curl, CURLOPT_POSTFIELDS, body.c_str());

            curl_res = curl_easy_perform(curl);
            if (curl_res != CURLE_OK) {
                std::cerr << "curl_easy_perform() failed: " << curl_easy_strerror(curl_res) << std::endl;
            }

            curl_easy_getinfo(curl, CURLINFO_RESPONSE_CODE, &http_code);
            res = std::string(memory.memory);

            curl_slist_free_all(chunk);
            curl_easy_cleanup(curl);
        }
        return http_code;
    }

    long getRequest(const std::string& url, nlohmann::json& res, const std::vector<std::string>& headers, const std::string& body) {
        std::string response;
        long http_code = downloadPage(url, response, headers, body);
        if (http_code == 200) {
            res = nlohmann::json::parse(response);
        }
        return http_code;
    }

private:
    MemoryStruct memory;
};

