#include <boost/asio.hpp>
#include <deque>
#include <memory>

using boost::asio::ip::tcp;

class chat_message {
public:
    enum { header_length = 4, max_body_length = 512 };
    char data_[header_length + max_body_length];

    size_t body_length() const { return *reinterpret_cast<const std::size_t*>(data_); }
    const char* body() const { return data_ + header_length; }
    char* body() { return data_ + header_length; }
    void resize_body(size_t new_length) { *reinterpret_cast<std::size_t*>(data_) = new_length; }
};

using chat_message_queue = std::deque<chat_message>;

class chat_client {
public:
    chat_client(boost::asio::io_context& io_context, const tcp::resolver::results_type& endpoints)
        : io_context_(io_context), socket_(io_context) {
        do_connect(endpoints);
    }

    void write(const chat_message& msg) {
        io_context_.post([this, msg]() {
            bool write_in_progress = !write_msgs_.empty();
            write_msgs_.push_back(msg);
            if (!write_in_progress) {
                do_write();
            }
        });
    }

    void close() {
        io_context_.post([this]() { socket_.close(); });
    }

private:
    void do_connect(const tcp::resolver::results_type& endpoints) {
        boost::asio::async_connect(socket_, endpoints,
            [this](boost::system::error_code ec, tcp::resolver::iterator) {
                if (!ec) {
                    do_read_header();
                }
            });
    }

    void do_read_header() {
        boost::asio::async_read(socket_, boost::asio::buffer(read_msg_.data_, chat_message::header_length),
            [this](boost::system::error_code ec, std::size_t) {
                if (!ec && read_msg_.body_length() > 0) {
                    do_read_body();
                } else {
                    close();
                }
            });
    }

    void do_read_body() {
        boost::asio::async_read(socket_, boost::asio::buffer(read_msg_.body(), read_msg_.body_length()),
            [this](boost::system::error_code ec, std::size_t) {
                if (!ec) {
                    // Process the message (not implemented here)
                    do_read_header();
                } else {
                    close();
                }
            });
    }

    void do_write() {
        boost::asio::async_write(socket_, boost::asio::buffer(write_msgs_.front().data_, write_msgs_.front().body_length() + chat_message::header_length),
            [this](boost::system::error_code ec, std::size_t) {
                if (!ec) {
                    write_msgs_.pop_front();
                    if (!write_msgs_.empty()) {
                        do_write();
                    }
                } else {
                    close();
                }
            });
    }

    boost::asio::io_context& io_context_;
    tcp::socket socket_;
    chat_message read_msg_;
    chat_message_queue write_msgs_;
};
