};

enum class ErrorCode {
    SUCCESS,
    CONNECTION_FAILED,
    TIMEOUT,
    // Other error codes
};

class ClientSocket {
public:
    ClientSocket(ThreadCtx& threadCtx, const std::string& dest, int port, const EncryptionParams& encryptionParams, int64_t ivChangeInterval)
        : threadCtx_(threadCtx), dest_(dest), peerIp_("") {
        memset(&sa_, 0, sizeof(sa_));
        sa_.ai_family = AF_UNSPEC; // IPv4 or IPv6
        sa_.ai_socktype = SOCK_STREAM;

        std::string portStr = std::to_string(port);
        int result = getaddrinfo(dest_.c_str(), portStr.c_str(), &sa_, &result_);
        if (result != 0) {
            throw std::runtime_error("getaddrinfo failed");
        }

        socket_ = std::make_unique<WdtSocket>(socket(result_->ai_family, result_->ai_socktype, result_->ai_protocol));
        if (socket_->get() < 0) {
            throw std::runtime_error("Socket creation failed");
        }
    }

    ErrorCode connect() {
        struct timeval timeout;
        timeout.tv_sec = 5; // 5 seconds timeout
        timeout.tv_usec = 0;

        setsockopt(socket_->get(), SOL_SOCKET, SO_SNDTIMEO, (const char*)&timeout, sizeof(timeout));

        int result = ::connect(socket_->get(), result_->ai_addr, result_->ai_addrlen);
        if (result < 0) {
            return (errno == EINPROGRESS) ? ErrorCode::TIMEOUT : ErrorCode::CONNECTION_FAILED;
        }

        peerIp_ = getPeerIp();
        return ErrorCode::SUCCESS;
    }

    const std::string& getPeerIp() {
        char ipstr[INET6_ADDRSTRLEN];
        if (result_->ai_family == AF_INET) {
            struct sockaddr_in* ipv4 = (struct sockaddr_in*)result_->ai_addr;
            inet_ntop(AF_INET, &(ipv4->sin_addr), ipstr, sizeof(ipstr));
        } else {
            struct sockaddr_in6* ipv6 = (struct sockaddr_in6*)result_->ai_addr;
            inet_ntop(AF_INET6, &(ipv6->sin6_addr), ipstr, sizeof(ipstr));
        }
        return peerIp_ = ipstr;
    }

    void setSendBufferSize() {
        setsockopt(socket_->get(), SOL_SOCKET, SO_SNDBUF, &threadCtx_.sendBufferSize, sizeof(threadCtx_.sendBufferSize));
    }

private:
    void closeConnection() {
        if (socket_) {
            socket_.reset();
        }
    }

    std::string dest_;
    ThreadCtx& threadCtx_;
    struct addrinfo sa_, *result_;
    std::unique_ptr<WdtSocket> socket_;
    std::string peerIp_;
};

