        }
        return fcntl(sockFD, F_SETFL, flags) != -1;
    }

    bool ConnectWithTimeout(SocketHolder sockFD, void* server_addr, size_t server_addr_len, int timeout_seconds) {
        SetNonBlock(sockFD, true);
        int result = connect(sockFD, (struct sockaddr*)server_addr, server_addr_len);
        if (result < 0 && errno != EINPROGRESS) return false;

