        return ioctlsocket(sockFD, FIONBIO, &mode) == 0;
#else
        return fcntl(sockFD, F_SETFL, nonBlocking ? (fcntl(sockFD, F_GETFL) | O_NONBLOCK) : (fcntl(sockFD, F_GETFL) & ~O_NONBLOCK)) != -1;
#endif
    }

    bool ConnectWithTimeout(SocketHolder sockFD, void* server_addr, size_t server_addr_len, int timeout_seconds) {
        SetNonBlock(sockFD, true);
        if (connect(sockFD, static_cast<struct sockaddr*>(server_addr), server_addr_len) < 0) {
            if (errno != EINPROGRESS) return false;
        }

        fd_set write_fds;
        FD_ZERO(&write_fds);
        FD_SET(sockFD, &write_fds);
        struct timeval tv;
        tv.tv_sec = timeout_seconds;
        tv.tv_usec = 0;

        int result = select(sockFD + 1, nullptr, &write_fds, nullptr, &tv);
        if (result > 0) {
            int so_error;
            socklen_t len = sizeof(so_error);
            if (getsockopt(sockFD, SOL_SOCKET, SO_ERROR, &so_error, &len) < 0 || so_error != 0) {
                return false;
            }
            return true;
        }
