class Stats {
public:
    Stats(const std::string& stats_socket_path) : stats_socket_path_(stats_socket_path), sockfd_(-1), statsThreadRunning_(false), thread_count_(0) {
        init(stats_socket_path);
    }

    ~Stats() {
        statsThreadRunning_ = false;
        if (stats_thread_.joinable()) {
            stats_thread_.join();
        }
        unlink(stats_socket_path_.c_str());
    }

    static Stats& get(const std::string& stats_socket_path) {
        static Stats instance(stats_socket_path);
        return instance;
    }

    static std::unique_ptr<Stats> get_for_unittest(const std::string& stats_socket_path) {
        return std::make_unique<Stats>(stats_socket_path);
    }

    static bool init(const std::string& stats_socket_path) {
        if (!isInit()) {
            get(stats_socket_path).startSocket();
            return true;
        }
        return false;
    }

    static bool isInit() {
        return instance_ != nullptr;
    }

    std::unordered_map<std::string, int> getAll() {
        std::lock_guard<std::mutex> lock(stats_mutex_);
        return stats_;
    }

    int increment(const std::string& key, int val) {
        std::lock_guard<std::mutex> lock(stats_mutex_);
        stats_[key] += val;
        return stats_[key];
    }

    int set(const std::string& key, int val) {
        std::lock_guard<std::mutex> lock(stats_mutex_);
        stats_[key] = val;
        return stats_[key];
    }

    int reset() {
        std::lock_guard<std::mutex> lock(stats_mutex_);
        stats_.clear();
        return 0;
    }

    bool startSocket() {
        sockfd_ = socket(AF_UNIX, SOCK_STREAM, 0);
        if (sockfd_ < 0) return false;

        sockaddr_un serv_addr;
        memset(&serv_addr, 0, sizeof(serv_addr));
        serv_addr.sun_family = AF_UNIX;
        strncpy(serv_addr.sun_path, stats_socket_path_.c_str(), sizeof(serv_addr.sun_path) - 1);

        unlink(stats_socket_path_.c_str());
        if (bind(sockfd_, (struct sockaddr*)&serv_addr, sizeof(serv_addr)) < 0) return false;
        if (listen(sockfd_, 5) < 0) return false;

        statsThreadRunning_ = true;
        stats_thread_ = std::thread(&Stats::runSocket, this);
        return true;
    }

    void runSocket() {
        while (statsThreadRunning_) {
            int client_sock = accept(sockfd_, nullptr, nullptr);
            if (client_sock >= 0) {
                std::thread(&Stats::processMsg, this, client_sock).detach();
            }
        }
    }

    void processMsg(int sockfd) {
        char buffer[256];
        memset(buffer, 0, sizeof(buffer));
        read(sockfd, buffer, sizeof(buffer) - 1);
        std::string msg(buffer);
        // Process the message (e.g., parse commands and update stats)
        close(sockfd);
    }

