class udp_communicator {
public:
    udp_communicator(boost::asio::io_context& io_context, unsigned short port)
        : socket_(io_context, boost::asio::ip::udp::endpoint(boost::asio::ip::udp::v4(), port)) {
        start_receive();
    }

    void start_receive() {
        socket_.async_receive_from(
            boost::asio::buffer(receive_buffer_), receiver_endpoint_,
            [this](const boost::system::error_code& error, std::size_t bytes_transferred) {
                handle_receive(error, bytes_transferred);
            });
    }

    void handle_receive(const boost::system::error_code& error, std::size_t bytes_transferred) {
        if (!error) {
            std::cout << "Received: " << std::string(receive_buffer_.data(), bytes_transferred) << std::endl;
            start_receive(); // Continue receiving
        }
    }

