using boost::asio::ip::tcp;
using boost::asio::ip::udp;

class server {
public:
    server(boost::asio::io_context& io_context, unsigned short port)
        : acceptor_(io_context, tcp::endpoint(tcp::v4(), port)),
          udp_socket_(io_context, udp::endpoint(udp::v4(), port)),
          next_frame_number_(0) {
        start_accept();
        start_broadcast_timer();
    }

private:
    using tcp_socket_ptr = std::shared_ptr<tcp::socket>;
    using control_request_ptr = std::shared_ptr<std::string>;
    using timer_ptr = std::shared_ptr<boost::asio::steady_timer>;

    tcp::acceptor acceptor_;
    boost::asio::steady_timer timer_;
    udp::socket udp_socket_;
    unsigned long next_frame_number_;
    std::set<udp::endpoint> subscribers_;

    void start_accept() {
        tcp_socket_ptr socket = std::make_shared<tcp::socket>(acceptor_.get_executor().context());
        acceptor_.async_accept(*socket, [this, socket](const boost::system::error_code& ec) {
            handle_accept(ec, socket);
        });
    }

    void handle_accept(const boost::system::error_code& ec, tcp_socket_ptr socket) {
        if (!ec) {
            // Start reading control requests from the socket
            auto request = std::make_shared<std::string>();
            boost::asio::async_read(*socket, boost::asio::buffer(*request), 
                [this, socket, request](const boost::system::error_code& ec, std::size_t) {
                    handle_control_request(ec, socket, request);
                });
            start_accept();
        }
    }

    void handle_control_request(const boost::system::error_code& ec, tcp_socket_ptr socket, control_request_ptr request) {
        if (!ec) {
            // Process the control request and manage subscriptions
            // Example: Parse request and add/remove subscribers
            // After processing, handle the next request
            start_accept();
        }
    }

    void start_broadcast_timer() {
        timer_.expires_after(std::chrono::seconds(1));
        timer_.async_wait([this](const boost::system::error_code& ec) {
            if (!ec) {
                handle_timer();
            }
        });
    }

    void handle_timer() {
        // Broadcast data frames to all subscribed clients
        for (const auto& subscriber : subscribers_) {
            std::string frame = "Frame " + std::to_string(next_frame_number_++);
            udp_socket_.send_to(boost::asio::buffer(frame), subscriber);
        }
        start_broadcast_timer();
    }
};

