                    rename(m_outputTmp.c_str(), m_output.c_str());
                    curl_easy_cleanup(curl);
                    return true;
                } else {
                    m_error = curl_easy_strerror(res);
                    m_retries++;
                }
                curl_easy_cleanup(curl);
            }
        }
        return false;
    }
};

#include <curl/curl.h>
#include <fstream>
#include <string>
#include <sys/stat.h>
#include <unistd.h>

class DownloadTask {
public:
    static void initialize() {
        curl_global_init(CURL_GLOBAL_DEFAULT);
    }

    static void uninitialize() {
        curl_global_cleanup();
    }

    DownloadTask(const std::string& url, const std::string& urlBackup, const std::string& output, time_t mtime, const std::string& name = "")
        : m_url(url), m_urlBackup(urlBackup), m_output(output), m_outputTmp(output + ".tmp"), m_default(""), m_mtime(mtime), m_retries(0), m_name(name), m_userAgent("libcurl-agent/1.0"), m_error("") {}

    bool run() {
        for (unsigned int attempt = 0; attempt < 3; ++attempt) {
            if (downloadFile(m_url) || downloadFile(m_urlBackup)) {
                if (rename(m_outputTmp.c_str(), m_output.c_str()) != 0) {
                    m_error = "Failed to rename temporary file.";
                    return false;
                }
                if (utime(m_output.c_str(), nullptr) != 0) {
                    m_error = "Failed to set modification time.";
                }
                return true;
            }
            ++m_retries;
        }
        return false;
    }

    unsigned int getRetries() const {
        return m_retries;
    }

private:
    std::string m_url;
    std::string m_urlBackup;
    std::string m_output;
    std::string m_outputTmp;
    std::string m_default;
    time_t m_mtime;
    unsigned int m_retries;
    std::string m_name;
    std::string m_userAgent;
    std::string m_error;

    static size_t writeData(void* ptr, size_t size, size_t nmemb, FILE* stream) {
        return fwrite(ptr, size, nmemb, stream);
    }

    bool downloadFile(const std::string& url) {
        CURL* curl = curl_easy_init();
        if (!curl) {
            m_error = "Failed to initialize curl.";
            return false;
        }

        FILE* fp = fopen(m_outputTmp.c_str(), "wb");
        if (!fp) {
            m_error = "Failed to open temporary file.";
            curl_easy_cleanup(curl);
            return false;
        }

        curl_easy_setopt(curl, CURLOPT_URL, url.c_str());
        curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, writeData);
        curl_easy_setopt(curl, CURLOPT_WRITEDATA, fp);
        curl_easy_setopt(curl, CURLOPT_USERAGENT, m_userAgent.c_str());
        curl_easy_setopt(curl, CURLOPT_FOLLOWLOCATION, 1L);

        CURLcode res = curl_easy_perform(curl);
        fclose(fp);
        curl_easy_cleanup(curl);

        if (res != CURLE_OK) {
            m_error = curl_easy_strerror(res);
            return false;
        }
        return true;
    }
};

