typedef websocketpp::server<websocketpp::config::asio> ws_echo_server;

class WebSocketServer {
public:
    WebSocketServer(asio::io_service& service, int port) 
        : service_(service), port_(port) {
        ws_server_.set_access_channels(websocketpp::log::alevel::all);
        ws_server_.clear_access_channels(websocketpp::log::alevel::frame_payload);
