#include <websocketpp/config/asio_no_tls.hpp>
#include <websocketpp/server.hpp>
#include <websocketpp/extensions/permessage_deflate/enabled.hpp>
#include <boost/asio/ip/tcp.hpp>
#include <websocketpp/common/thread.hpp>
using websocketpp::lib::placeholders::_1;
using websocketpp::lib::placeholders::_2;
using websocketpp::lib::bind;
typedef websocketpp::server<websocketpp::config::asio> server;
typedef server::message_ptr message_ptr;

class WebSocketServer {
public:
    WebSocketServer(short port = 9002, size_t num_threads = 1) 
        : port_(port), num_threads_(num_threads) {}

    void run() {
        try {
            testee_server_.clear_access_channels(websocketpp::log::alevel::all);
            testee_server_.clear_error_channels(websocketpp::log::alevel::all);

            testee_server_.init_asio();
            testee_server_.set_reuse_addr(true);

            testee_server_.set_message_handler(bind(&WebSocketServer::on_message, this, ::_1, ::_2));
            testee_server_.set_socket_init_handler(bind(&WebSocketServer::on_socket_init, this, ::_1, ::_2));

            testee_server_.set_listen_backlog(8192);
            testee_server_.listen(port_);
            testee_server_.start_accept();

            if (num_threads_ == 1) {
                testee_server_.run();
            } else {
                std::vector<websocketpp::lib::shared_ptr<websocketpp::lib::thread>> ts;
                for (size_t i = 0; i < num_threads_; i++) {
                    ts.push_back(websocketpp::lib::make_shared<websocketpp::lib::thread>(&server::run, &testee_server_));
                }

                for (size_t i = 0; i < num_threads_; i++) {
                    ts[i]->join();
                }
            }
        } catch (websocketpp::exception const & e) {
            std::cout << "exception: " << e.what() << std::endl;
        }
    }

private:
    void on_message(websocketpp::connection_hdl hdl, message_ptr msg) {
        testee_server_.send(hdl, msg->get_payload(), msg->get_opcode());
    }

    void on_socket_init(websocketpp::connection_hdl, boost::asio::ip::tcp::socket & s) {
        boost::asio::ip::tcp::no_delay option(true);
        s.set_option(option);
    }

    server testee_server_;
    short port_;
    size_t num_threads_;
};

